CREATE OR REPLACE PROCEDURE importer_kairos_tools.save_drugs ( cd_composto_p COMPOSTO_MATGLOBAL.CD_COMPOSTO%type, ds_composto_p COMPOSTO_MATGLOBAL.DS_COMPOSTO%type, ie_situation_p COMPOSTO_MATGLOBAL.IE_SITUACAO%type DEFAULT 'A') AS $body$
DECLARE


cd_composto_w 			COMPOSTO_MATGLOBAL.CD_COMPOSTO%type;


BEGIN
	SELECT 	MAX(a.CD_COMPOSTO)
	INTO STRICT 	cd_composto_w
	FROM 	COMPOSTO_MATGLOBAL a
	WHERE 	trim(both LOWER(a.DS_COMPOSTO)) = trim(both LOWER(ds_composto_p));

	IF ((cd_composto_w IS NOT NULL AND cd_composto_w::text <> '') AND cd_composto_w <> cd_composto_p) THEN
		CALL importer_kairos_tools.check_conversion_rule(
			nm_table_p 		=> 'COMPOSTO_MATGLOBAL',
			nm_attribute_p 		=> 'CD_COMPOSTO',
			cd_external_p 		=> cd_composto_p,
			cd_internal_p 		=> cd_composto_w
		);
	ELSIF (coalesce(cd_composto_w::text, '') = '') THEN
		CALL importer_kairos_tools.validate_cd_drugs(cd_composto_p, ds_composto_p, ie_situation_p);
	END IF;
END;

$body$
