CREATE OR REPLACE PROCEDURE importer_kairos_tools.save_laboratory ( cd_laboratory_p LABORATORIO_MATGLOBAL.CD_LABORATORIO%type, ds_laboratory_p LABORATORIO_MATGLOBAL.DS_LABORATORIO%type, ie_situation_p LABORATORIO_MATGLOBAL.IE_SITUACAO%type DEFAULT 'A') AS $body$
DECLARE


cd_laboratory_w 	LABORATORIO_MATGLOBAL.CD_LABORATORIO%type;


BEGIN
	SELECT 	MAX(a.CD_LABORATORIO)
	INTO STRICT 	cd_laboratory_w
	FROM 	LABORATORIO_MATGLOBAL a
	WHERE 	trim(both LOWER(a.DS_LABORATORIO)) = trim(both LOWER(ds_laboratory_p));

	IF ((cd_laboratory_w IS NOT NULL AND cd_laboratory_w::text <> '') AND cd_laboratory_w <> cd_laboratory_p) THEN
		CALL importer_kairos_tools.check_conversion_rule(
			nm_table_p 	=> 'LABORATORIO_MATGLOBAL',
			nm_attribute_p 	=> 'CD_LABORATORIO',
			cd_external_p 	=> cd_laboratory_p,
			cd_internal_p 	=> cd_laboratory_w
		);
	ELSIF (coalesce(cd_laboratory_w::text, '') = '') THEN
		CALL importer_kairos_tools.validate_cd_laboratory(cd_laboratory_p, ds_laboratory_p, ie_situation_p);
	END IF;
END;

$body$
