CREATE OR REPLACE PROCEDURE importer_kairos_tools.save_presentation ( cd_unit_p UNIDADE_MEDIDA_MATGLOBAL.CD_UNIDADE%type, ds_unit_p UNIDADE_MEDIDA_MATGLOBAL.DS_UNIDADE%type, cd_internal_p MATERIAL_GLOBAL.CD_MATERIAL_GLO%type, ie_situation_p UNIDADE_MEDIDA_MATGLOBAL.IE_SITUACAO%type DEFAULT 'A') AS $body$
DECLARE


cd_unit_w	UNIDADE_MEDIDA_MATGLOBAL.CD_UNIDADE%type;


BEGIN
	SELECT 	MAX(a.CD_UNIDADE)
	INTO STRICT 	cd_unit_w
	FROM	UNIDADE_MEDIDA_MATGLOBAL a
	WHERE	a.CD_UNIDADE = cd_internal_p || cd_unit_p;

	IF (coalesce(cd_unit_w::text, '') = '') THEN
		CALL importer_kairos_tools.insert_presentation(cd_internal_p || cd_unit_p, ds_unit_p, ie_situation_p);
	END IF;
END;

$body$
