SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE importer_kairos_tools.validate_cd_drugs ( cd_composto_p COMPOSTO_MATGLOBAL.CD_COMPOSTO%type, ds_composto_p COMPOSTO_MATGLOBAL.DS_COMPOSTO%type, ie_situation_p COMPOSTO_MATGLOBAL.IE_SITUACAO%type DEFAULT 'A') AS $body$
DECLARE


cd_composto_w 			LABORATORIO_MATGLOBAL.CD_LABORATORIO%type;
cd_composto_new_w 		LABORATORIO_MATGLOBAL.CD_LABORATORIO%type;


BEGIN
	SELECT  MAX(a.CD_COMPOSTO)
	INTO STRICT    cd_composto_w
	FROM    COMPOSTO_MATGLOBAL a
	WHERE   a.CD_COMPOSTO = cd_composto_p;

	IF (coalesce(cd_composto_w::text, '') = '') THEN
		CALL importer_kairos_tools.insert_drugs(cd_composto_p, ds_composto_p, ie_situation_p);
	ELSE
		SELECT MAX(a.CD_COMPOSTO + 1)
		KEEP(DENSE_RANK LAST ORDER BY (REGEXP_REPLACE(a.CD_COMPOSTO, '[^[:digit:]]', ''))::numeric ) LAST_SEQUENCE
		INTO STRICT    cd_composto_new_w
		FROM    COMPOSTO_MATGLOBAL a;

		CALL importer_kairos_tools.insert_drugs(cd_composto_new_w, ds_composto_p, ie_situation_p);

		CALL importer_kairos_tools.check_conversion_rule(
			nm_table_p	=> 'COMPOSTO_MATGLOBAL',
			nm_attribute_p	=> 'CD_COMPOSTO',
			cd_external_p	=> cd_composto_p,
			cd_internal_p	=> cd_composto_new_w
		);
	END IF;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
