SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE importer_kairos_tools.validate_cd_product ( cd_product_p MATERIAL_GLOBAL.CD_MATERIAL_GLO%type, ds_product_p MATERIAL_GLOBAL.DS_MATERIAL_GLO%type, cd_laboratory_p LABORATORIO_MATGLOBAL.CD_LABORATORIO%type, cd_sell_type_p TIPO_VENDA_MATGLOBAL.CD_TIPO_VENDA%type, ie_importado_p MATERIAL_GLOBAL.IE_IMPORTADO%type, ie_situation_p MATERIAL_GLOBAL.IE_SITUACAO%type ) AS $body$
DECLARE


cd_product_w 			MATERIAL_GLOBAL.CD_MATERIAL_GLO%type;
cd_product_new_w 		MATERIAL_GLOBAL.CD_MATERIAL_GLO%type;


BEGIN
	SELECT 	MAX(a.CD_MATERIAL_GLO)
	INTO STRICT 	cd_product_w
	FROM 	MATERIAL_GLOBAL a
	WHERE	a.CD_MATERIAL_GLO = cd_product_p;

	IF (coalesce(cd_product_w::text, '') = '') THEN
		CALL importer_kairos_tools.insert_product(cd_product_p, ds_product_p, cd_laboratory_p, cd_sell_type_p, ie_importado_p, ie_situation_p);
		CALL importer_kairos_tools.set_cd_interno_product(
			cd_product_p	=> cd_product_p,
			cd_internal_p 	=> cd_product_p
		);
	ELSE
		SELECT MAX(a.CD_MATERIAL_GLO + 1)
		KEEP(DENSE_RANK LAST ORDER BY (REGEXP_REPLACE(a.CD_MATERIAL_GLO, '[^[:digit:]]', ''))::numeric ) LAST_SEQUENCE
		INTO STRICT 	cd_product_new_w
		FROM 	MATERIAL_GLOBAL a;

		CALL importer_kairos_tools.insert_product(cd_product_new_w, ds_product_p, cd_laboratory_p, cd_sell_type_p, ie_importado_p, ie_situation_p);

		CALL importer_kairos_tools.set_cd_interno_product(
			cd_product_p	=> cd_product_new_w,
			cd_internal_p 	=> cd_product_p
		);
	END IF;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
