CREATE OR REPLACE PROCEDURE importer_kairos_tools.validate_cd_sell_type ( cd_sell_type_p TIPO_VENDA_MATGLOBAL.CD_TIPO_VENDA%type, ds_sell_type_p TIPO_VENDA_MATGLOBAL.DS_TIPO_VENDA%type ) AS $body$
DECLARE


cd_sell_type_w 		TIPO_VENDA_MATGLOBAL.CD_TIPO_VENDA%type;
cd_sell_type_new_w 	TIPO_VENDA_MATGLOBAL.CD_TIPO_VENDA%type;


BEGIN
	SELECT 	MAX(a.CD_TIPO_VENDA)
  	INTO STRICT 	cd_sell_type_w
  	FROM 	TIPO_VENDA_MATGLOBAL a
  	WHERE	a.CD_TIPO_VENDA = cd_sell_type_p;

  	IF (coalesce(cd_sell_type_w::text, '') = '') THEN
		CALL importer_kairos_tools.insert_sell_type(cd_sell_type_p, ds_sell_type_p);  	
  	ELSE
  		SELECT MAX(a.CD_TIPO_VENDA + 1)
		KEEP(DENSE_RANK LAST ORDER BY (REGEXP_REPLACE(a.CD_TIPO_VENDA, '[^[:digit:]]', ''))::numeric ) LAST_SEQUENCE
  		INTO STRICT 	cd_sell_type_new_w
  		FROM 	TIPO_VENDA_MATGLOBAL a;

  		CALL importer_kairos_tools.insert_sell_type(cd_sell_type_new_w, ds_sell_type_p);

		CALL importer_kairos_tools.check_conversion_rule(
			nm_table_p	=> 'TIPO_VENDA_MATGLOBAL',
			nm_attribute_p 	=> 'CD_TIPO_VENDA',
			cd_external_p 	=> cd_sell_type_p,
			cd_internal_p 	=> cd_sell_type_new_w
		);
  	END IF;
END;

$body$
