CREATE OR REPLACE PROCEDURE integracao_athena_disp_pck.suspender_item_prescricao ( nr_prescricao_p bigint, nr_seq_material_p bigint, nr_seq_horario_p bigint, nm_usuario_p text, nr_opcao_p bigint, nr_seq_lote_p bigint DEFAULT NULL) AS $body$
DECLARE
	-- Change this to reflect the dblink connection string
	v_conn_str  text := format('port=%s dbname=%s user=%s', current_setting('port'), current_database(), current_user);
	v_query     text;

BEGIN
	v_query := 'CALL integracao_athena_disp_pck.suspender_item_prescricao_atx ( ' || quote_nullable(nr_prescricao_p) || ',' || quote_nullable(nr_seq_material_p) || ',' || quote_nullable(nr_seq_horario_p) || ',' || quote_nullable(nm_usuario_p) || ',' || quote_nullable(nr_opcao_p) || ',' || quote_nullable(nr_seq_lote_p) || ' )';
