CREATE OR REPLACE PROCEDURE integracao_athena_disp_pck.suspender_item_prescricao_atx ( nr_prescricao_p bigint, nr_seq_material_p bigint, nr_seq_horario_p bigint, nm_usuario_p text, nr_opcao_p bigint, nr_seq_lote_p bigint DEFAULT NULL) AS $body$
DECLARE


        /*
            nr_opcao_p
            1 - Suspender item prescricao
            2 - Suspender item / horario prescricao
        */


        cd_setor_atendimento_w   setor_atendimento.cd_setor_atendimento%TYPE;
        cd_estabelecimento_w     estabelecimento.cd_estabelecimento%TYPE;
        nr_seq_estrut_int_w      mat_estrutura.nr_sequencia%TYPE;
        ie_estrutura_w           varchar(1) := 'N';
        ie_setor_athena_w      varchar(1) := 'N';
        ds_param_integ_hl7_w     varchar(4000);
        ie_urgente_w             varchar(1);
        ie_enviar_horarios_disp_w	varchar(1);
BEGIN
        BEGIN
            SELECT cd_setor_atendimento,
                cd_estabelecimento
            INTO STRICT cd_setor_atendimento_w,
                cd_estabelecimento_w
            FROM prescr_medica
            WHERE nr_prescricao = nr_prescricao_p;

            SELECT 'S'
            INTO STRICT ie_setor_athena_w
            FROM far_setores_integracao
            WHERE nr_seq_empresa_int = current_setting('integracao_athena_disp_pck.nr_seq_empresa_integracao_w')::far_setores_integracao.nr_seq_empresa_int%type
            AND cd_setor_atendimento = cd_setor_atendimento_w;

            select coalesce(max(ie_enviar_horarios_disp), 'N')
            into STRICT ie_enviar_horarios_disp_w
            from parametros_farmacia
            where cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

        EXCEPTION
            WHEN OTHERS THEN
                ie_setor_athena_w := 'N';
        END;

        SELECT coalesce(MAX('S'), 'N')
        INTO STRICT ie_urgente_w
        FROM ap_lote a,
            classif_lote_disp_far b
        WHERE a.nr_sequencia = nr_seq_lote_p
        AND b.nr_sequencia = a.nr_seq_classif
        AND b.ie_classif_urgente = 'A';

        IF ( ie_setor_athena_w = 'S' ) AND (ie_urgente_w = 'S' OR ie_enviar_horarios_disp_w = 'S') THEN
            
            IF nr_opcao_p = 1 THEN
			
			   ds_param_integ_hl7_w := 'nr_prescricao=' || nr_prescricao_p || obter_separador_bv
						|| 'nr_seq_material=' || nr_seq_material_p || obter_separador_bv
						|| 'ie_enviar_horarios_disp=' || ie_enviar_horarios_disp_w || obter_separador_bv
						|| 'nr_seq_horario=' || null || obter_separador_bv
						|| 'nr_seq_lote=' || null || obter_separador_bv;
						
                CALL gravar_agend_integracao(437, ds_param_integ_hl7_w);

            ELSIF nr_opcao_p = 2 THEN

                ds_param_integ_hl7_w := 'nr_prescricao=' || nr_prescricao_p || obter_separador_bv
                        || 'nr_seq_material=' || nr_seq_material_p || obter_separador_bv
                        || 'nr_seq_horario=' || nr_seq_horario_p || obter_separador_bv
                        || 'ie_enviar_horarios_disp=' || ie_enviar_horarios_disp_w || obter_separador_bv;

                CALL gravar_agend_integracao(925, ds_param_integ_hl7_w);

            END IF;
                                           
        END IF;


        
        
    END;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
