SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE integracao_titulo_pagar_pck.excluir_titulo_pagar (cd_filial_p text, cd_fornecedor_p text, nr_titulo_p text, nr_fatura_p text, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

 
	nr_titulo_w		bigint;
	nr_lote_contabil_w		bigint;
	ds_erro_w		varchar(255);
	
	
BEGIN 
	 
	select	coalesce(max(nr_titulo),0) 
	into STRICT	nr_titulo_w 
	from	titulo_pagar 
	where 	nr_titulo_externo = nr_titulo_p;
	 
	select	coalesce(max(nr_lote_contabil),0) 
	into STRICT	nr_lote_contabil_w 
	from	titulo_pagar 
	where	nr_titulo	= nr_titulo_w;
	 
	if (nr_lote_contabil_w > 0) then 
		ds_erro_w	:= substr(Wheb_mensagem_pck.get_texto(303551,'NR_TITULO_W=' || nr_titulo_w) ,1,255);
	end if;
	 
	if (coalesce(ds_erro_w,'X') = 'X') and (nr_titulo_w <> 0) then 
		begin 
		 
		delete	from titulo_pagar 
		where	nr_titulo	= nr_titulo_w;
		 
		insert into log_tasy( 
			cd_log, 
			ds_log, 
			nm_usuario, 
			dt_atualizacao) 
		values (	55788, 
			Wheb_mensagem_pck.get_texto(303549) || ' ' || nr_titulo_w || '/' || nr_titulo_p, 
			nm_usuario_p, 
			clock_timestamp());
		exception when others then 
			ds_erro_w	:= substr(Wheb_mensagem_pck.get_texto(303550) || ' ' || nr_titulo_w || chr(13) || sqlerrm(SQLSTATE),1,255);
			 
		end;
	end if;
	 
	ds_erro_p	:= ds_erro_w;
	/*commit; estas procedures não podem ter commit porque possuem parâmetros out via dblink erro orcl*/
 
	end;
	

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
