CREATE OR REPLACE FUNCTION ish_case_proc_data_pck.get_procedure_list_request ( nr_seq_fila_p bigint) RETURNS SETOF T_PROCEDURE_LIST_REQUEST AS $body$
DECLARE


nr_seq_documento_w		intpd_fila_transmissao.nr_seq_documento%type;
r_procedure_list_request_w		r_procedure_list_request;
reg_integracao_w			gerar_int_padrao.reg_integracao_conv;
nr_seq_projeto_xml_w		intpd_eventos_sistema.nr_seq_projeto_xml%type;
institute_w			hcm_fall.institute%type;
caseno_w			hcm_fall.caseno%type;
patientno_w			hcm_patienten.patientno%type;
movenumber_w			hcm_fall.movenumber%type;
sendclient_w			hcm_kopf.sendclient%type;
ie_geracao_w			intpd_fila_transmissao.ie_geracao%type;


BEGIN

select	max(a.nr_seq_documento),
	max(b.nr_seq_projeto_xml),
	max(a.ie_geracao)
into STRICT	nr_seq_documento_w,
	nr_seq_projeto_xml_w,
	ie_geracao_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia	= nr_seq_fila_p;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);

reg_integracao_w.nm_tabela 			:= 'HCM_FALL';
reg_integracao_w.nm_elemento		:= 'urn:CaseprocedureGetlist';

if (ie_geracao_w = 'U') then
	ish_get_manual_values(nr_seq_fila_p, patientno_w, caseno_w, movenumber_w, sendclient_w, institute_w);
else
	if (nr_seq_documento_w IS NOT NULL AND nr_seq_documento_w::text <> '') then
		select	max(institute),
			max(caseno)
		into STRICT	institute_w,
			caseno_w
		from	hcm_fall
		where	nr_sequencia	= nr_seq_documento_w;		
	end if;
end if;

intpd_processar_atrib_envio(reg_integracao_w, 'INSTITUTE', 'institution', 'N', institute_w, 'N', r_procedure_list_request_w.institution);
intpd_processar_atrib_envio(reg_integracao_w, 'CASENO', 'patcaseid', 'N', caseno_w, 'N', r_procedure_list_request_w.patcaseid);

r_procedure_list_request_w.completesurgerydata := 'X';
r_procedure_list_request_w.onlyofficialcodes := null;
r_procedure_list_request_w.maxcnt := null;
r_procedure_list_request_w.cancelledincl	:= 'X';

RETURN NEXT r_procedure_list_request_w;
CALL intpd_gravar_log_fila(reg_integracao_w);
END;

$body$
