/*-------------------
caseservicecancelmult - request
*/
CREATE OR REPLACE FUNCTION ish_case_serv_data_pck.get_service_cancel_mult (nr_seq_fila_p bigint) RETURNS SETOF T_SERVICE_CANCEL_MULT AS $body$
DECLARE


r_service_cancel_mult_w		r_service_cancel_mult;
nr_sequencia_w			procedimento_paciente.nr_sequencia%type;
cd_empresa_w			estabelecimento.cd_empresa%type;
current_setting('ish_case_serv_data_pck.cd_estabelecimento_w')::estabelecimento.cd_estabelecimento%type		estabelecimento.cd_estabelecimento%type;
nr_seq_episodio_w		atendimento_paciente.nr_seq_episodio%type;
nr_episodio_w			episodio_paciente.nr_episodio%type;
serviceseqno_w			varchar(100);
reg_integracao_w		gerar_int_padrao.reg_integracao_conv;


BEGIN
intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);

--o agrupador considerado para amazenar o numero do atendimento do procedimento
--entao precisamos buscar todos os itens deste mesmo agrupador que estao na fila de integracao
select	max(nr_seq_documento)
into STRICT	nr_sequencia_w
from	intpd_fila_transmissao
where	nr_sequencia	= nr_seq_fila_p;

select	max(a.cd_empresa),
	max(a.cd_estabelecimento),
	max(b.nr_seq_episodio)
into STRICT	cd_empresa_w,
	current_setting('ish_case_serv_data_pck.cd_estabelecimento_w')::estabelecimento.cd_estabelecimento%type,
	nr_seq_episodio_w
from	estabelecimento a,
	atendimento_paciente b,
	procedimento_paciente c
where	a.cd_estabelecimento = b.cd_estabelecimento
and	b.nr_atendimento = c.nr_atendimento
and	c.nr_sequencia = nr_sequencia_w;

select	max(nr_episodio)
into STRICT	nr_episodio_w
from	episodio_paciente
where	nr_sequencia		= nr_seq_episodio_w;

reg_integracao_w.nm_tabela 			:= 'ESTABELECIMENTO';
reg_integracao_w.nm_elemento			:= 'urn:CaseserviceCancelmult';

intpd_processar_atrib_envio(reg_integracao_w, 'CD_EMPRESA', 'client', 'N', cd_empresa_w, 'S', r_service_cancel_mult_w.client);
intpd_processar_atrib_envio(reg_integracao_w, 'CD_ESTABELECIMENTO', 'institution', 'N', current_setting('ish_case_serv_data_pck.cd_estabelecimento_w')::estabelecimento.cd_estabelecimento%type, 'S', r_service_cancel_mult_w.institution);
reg_integracao_w.nm_tabela 	:= 'EPISODIO_PACIENTE';
intpd_processar_atrib_envio(reg_integracao_w, 'NR_EPISODIO', 'patcaseid', 'N', nr_episodio_w, 'N', r_service_cancel_mult_w.patcaseid);

reg_integracao_w.nm_tabela 	:= 'PROCEDIMENTO_PACIENTE';
reg_integracao_w.nm_elemento	:= 'CaseserviceCancel';
intpd_processar_atrib_envio(reg_integracao_w, 'NR_SEQUENCIA', 'serviceseqno', 'N', nr_sequencia_w, 'S', serviceseqno_w);
r_service_cancel_mult_w.serviceseqno	:= obter_valor_campo_separador(serviceseqno_w,2,current_setting('ish_case_serv_data_pck.ds_separador_w')::varchar(10));
--r_procedure_cancel_mult_w.ruletype	:= 
RETURN NEXT r_service_cancel_mult_w;

CALL intpd_gravar_log_fila(reg_integracao_w);
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
