SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE ish_diagnosis_pck.record_diag_medico ( diagnostico_medico_p INOUT diagnostico_medico, diagnostico_doenca_p INOUT diagnostico_doenca, reg_integracao_p INOUT gerar_int_padrao.reg_integracao_conv) AS $body$
BEGIN
begin
select  *
into STRICT    diagnostico_medico_p
from    diagnostico_medico
where   nr_atendimento = diagnostico_doenca_p.nr_atendimento
and 	dt_diagnostico = diagnostico_doenca_p.dt_diagnostico  LIMIT 1;
exception
when others then
	diagnostico_medico_p.nr_atendimento :=  null;
end;

if (coalesce(diagnostico_medico_p.nr_atendimento::text, '') = '') then
	begin
	select  max(cd_medico_atendimento)
	into STRICT    diagnostico_medico_p.cd_medico
	from    atendimento_paciente
	where   nr_atendimento = diagnostico_doenca_p.nr_atendimento;

	diagnostico_medico_p.nr_atendimento     :=  diagnostico_doenca_p.nr_atendimento;
	diagnostico_medico_p.dt_diagnostico     :=  diagnostico_doenca_p.dt_diagnostico;
	diagnostico_medico_p.ie_tipo_diagnostico        :=  2;
	intpd_processar_atributo(reg_integracao_p, 'NM_USUARIO', current_setting('ish_diagnosis_pck.usernametasy')::varchar(15), 'N', diagnostico_medico_p.nm_usuario);
	intpd_processar_atributo(reg_integracao_p, 'DT_ATUALIZACAO', clock_timestamp(), 'N', diagnostico_medico_p.dt_atualizacao);

	insert into diagnostico_medico values (diagnostico_medico_p.*);
	end;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
