CREATE OR REPLACE FUNCTION ish_outpat_case_pck.get_add_outpat_visit_cas ( nr_seq_fila_p bigint) RETURNS SETOF T_ADD_OUTPAT_VISIT_CAS AS $body$
DECLARE

		
r_add_outpat_visit_cas_w	r_add_outpat_visit_cas;

nr_seq_documento_w		intpd_fila_transmissao.nr_seq_documento%type;
ie_conversao_w			intpd_eventos_sistema.ie_conversao%type;
nr_seq_regra_w			intpd_eventos_sistema.nr_seq_regra_conv%type;
	
nr_atendimento_w		atendimento_paciente.nr_atendimento%type;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
cd_empresa_w			empresa.cd_empresa%type;
reg_integracao_w		gerar_int_padrao.reg_integracao_conv;
nr_episodio_w			episodio_paciente.nr_episodio%type;


BEGIN
select	a.nr_seq_documento,
	coalesce(b.ie_conversao,'I'),
	b.nr_seq_regra_conv
into STRICT	nr_seq_documento_w,
	ie_conversao_w,	
	nr_seq_regra_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia = nr_seq_fila_p;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);
reg_integracao_w.nm_tabela 			:= 'EPISODIO_PACIENTE';
reg_integracao_w.nm_elemento			:= 'PatcaseAddoutpatvisitcas';

begin

select	a.nr_episodio
into STRICT	nr_episodio_w
from	episodio_paciente a,
	atendimento_paciente b
where	a.nr_sequencia = b.nr_seq_episodio
and	b.nr_atendimento = nr_seq_documento_w;

nr_atendimento_w := nr_seq_documento_w;

select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	atendimento_paciente
where	nr_atendimento = nr_atendimento_w;

select	cd_empresa
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento = cd_estabelecimento_w;

select	max(c.cd_pessoa_fisica_externo)
into STRICT	r_add_outpat_visit_cas_w.patientid
from	episodio_paciente a,
	pessoa_fisica b,
	pf_codigo_externo c
where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
and	b.cd_pessoa_fisica = c.cd_pessoa_fisica
and	a.nr_sequencia in (SELECT x.nr_seq_episodio from atendimento_paciente x where x.nr_atendimento = nr_atendimento_w);

reg_integracao_w.nm_tabela 			:= 'ESTABELECIMENTO';
intpd_processar_atrib_envio(reg_integracao_w, 'CD_EMPRESA', 'client', 'N', cd_empresa_w, 'S', r_add_outpat_visit_cas_w.client);
reg_integracao_w.nm_tabela 			:= 'ATENDIMENTO_PACIENTE';
intpd_processar_atrib_envio(reg_integracao_w, 'CD_ESTABELECIMENTO', 'institution', 'N',cd_estabelecimento_w, 'S', r_add_outpat_visit_cas_w.institution);

if (nr_episodio_w IS NOT NULL AND nr_episodio_w::text <> '') then
	reg_integracao_w.nm_tabela 	:= 'EPISODIO_PACIENTE';
	intpd_processar_atrib_envio(reg_integracao_w, 'NR_EPISODIO', 'patcaseid', 'N', nr_episodio_w, 'N', r_add_outpat_visit_cas_w.patcaseid);
	r_add_outpat_visit_cas_w.transactmode	:=	'S';
else	
	r_add_outpat_visit_cas_w.transactmode	:=	'N';
end if;

exception
when others then
	null;
end;

RETURN NEXT r_add_outpat_visit_cas_w;
CALL intpd_gravar_log_fila(reg_integracao_w);
END;

$body$
LANGUAGE PLPGSQL
