CREATE OR REPLACE PROCEDURE ish_param_pck.set_conv_tipo_adm_fat () AS $body$
DECLARE


qt_reg_w	bigint;


BEGIN

begin
select	1
into STRICT	qt_reg_w
from	conversao_meio_externo
where	length(cd_externo) - length(replace(cd_externo,get_separador,null)) = 1
and	nm_atributo like 'NR_SEQ_TIPO_ADMISSAO_FAT'
and	nm_tabela like 'ATENDIMENTO_PACIENTE'  LIMIT 1;
exception
when others then
	begin
	select	1
	into STRICT	qt_reg_w
	from	conversao_meio_externo
	where	length(cd_externo) - length(replace(cd_externo,get_separador,null)) = 1
	and	nm_tabela like 'TIPO_ADMISSAO_FAT'
	and	nm_atributo like 'NR_SEQUENCIA'  LIMIT 1;
	exception
	when others then
		qt_reg_w	:=	0;
	end;
end;

if (qt_reg_w > 0) then
	PERFORM set_config('ish_param_pck.ie_conv_tipo_adm_fat_w', 'MCMT', false);
else
	PERFORM set_config('ish_param_pck.ie_conv_tipo_adm_fat_w', 'CTMCMT', false);
end if;

END;

