CREATE OR REPLACE FUNCTION ish_pat_case_pck.get_cancel_dis_user_date ( nr_seq_fila_p bigint) RETURNS SETOF T_CANCEL_DIS_USER_DATE AS $body$
DECLARE

		
r_cancel_dis_user_date_w		r_cancel_dis_user_date;

nr_seq_documento_w		intpd_fila_transmissao.nr_seq_documento%type;
ie_conversao_w			intpd_eventos_sistema.ie_conversao%type;
nr_seq_regra_w			intpd_eventos_sistema.nr_seq_regra_conv%type;

nr_atendimento_w			atendimento_paciente.nr_atendimento%type;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
cd_empresa_w			empresa.cd_empresa%type;
cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;
nr_seq_atend_aux_w		atend_paciente_auxiliar.nr_sequencia%type;
reg_integracao_w		gerar_int_padrao.reg_integracao_conv;
dt_atualizacao_w		atend_paciente_auxiliar.dt_atualizacao%type;
nr_seq_motivo_est_alta_w	atend_paciente_auxiliar.nr_seq_motivo_est_alta%type;
dt_geral_w			varchar(255);


BEGIN
select	a.nr_seq_documento,
	coalesce(b.ie_conversao,'I'),
	b.nr_seq_regra_conv
into STRICT	nr_seq_documento_w,
	ie_conversao_w,	
	nr_seq_regra_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia = nr_seq_fila_p;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);

begin
select	max(nr_sequencia)
into STRICT	nr_seq_atend_aux_w
from	atend_paciente_auxiliar
where	nr_atendimento = nr_seq_documento_w;

select	dt_atualizacao,
	nr_seq_motivo_est_alta
into STRICT	dt_atualizacao_w,
	nr_seq_motivo_est_alta_w
from	atend_paciente_auxiliar
where	nr_sequencia = nr_seq_atend_aux_w;


reg_integracao_w.nm_tabela 		:= 'ATEND_PACIENTE_AUXILIAR';
reg_integracao_w.nm_elemento		:= 'CancelUserDate';
intpd_processar_atrib_envio(reg_integracao_w, 'NM_USUARIO', 'canceluser', 'N', coalesce(ish_param_pck.get_nm_usuario,current_setting('ish_pat_case_pck.usernameish')::varchar(15)), 'N', r_cancel_dis_user_date_w.canceluser);
r_cancel_dis_user_date_w.canceluserx	:= 'X';
intpd_processar_atrib_envio(reg_integracao_w, 'DT_ATUALIZACAO', 'canceldate', 'N', dt_atualizacao_w, 'N', dt_geral_w);
r_cancel_dis_user_date_w.canceldate	:= to_char(to_date(dt_geral_w),'yyyy-mm-dd');
intpd_processar_atrib_envio(reg_integracao_w, 'DT_ATUALIZACAO', 'canceldatex', 'S', 'X', 'N', r_cancel_dis_user_date_w.canceldatex);
intpd_processar_atrib_envio(reg_integracao_w, 'NR_SEQ_MOTIVO_EST_ALTA', 'cancelreason', 'N', nr_seq_motivo_est_alta_w, 'S', r_cancel_dis_user_date_w.cancelreason);
intpd_processar_atrib_envio(reg_integracao_w, 'NR_SEQ_MOTIVO_EST_ALTA', 'cancelreasonx', 'S', 'X', 'N', r_cancel_dis_user_date_w.cancelreasonx);

exception
when others then
	null;
end;

RETURN NEXT r_cancel_dis_user_date_w;
CALL intpd_gravar_log_fila(reg_integracao_w);
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
