CREATE OR REPLACE FUNCTION ish_pat_case_pck.get_cancel_user_date ( nr_seq_fila_p bigint) RETURNS SETOF T_CANCEL_USER_DATE AS $body$
DECLARE


r_cancel_user_date_w	r_cancel_user_date;

nr_seq_documento_w	intpd_fila_transmissao.nr_seq_documento%type;
nr_seq_episodio_w	episodio_paciente.nr_sequencia%type;
nr_seq_regra_w		intpd_eventos_sistema.nr_seq_regra_conv%type;
reg_integracao_w	gerar_int_padrao.reg_integracao_conv;
dt_cancelamento_w	atendimento_paciente.dt_cancelamento%type;
nr_seq_motivo_w		bigint;
dt_geral_w		varchar(255);

C01 CURSOR FOR
SELECT	b.nr_seq_motivo,	
	a.dt_cancelamento
FROM atendimento_paciente a
LEFT OUTER JOIN atendimento_cancelamento b ON (a.nr_atendimento = b.nr_atendimento)
WHERE a.nr_seq_episodio = nr_seq_episodio_w and get_case_encounter_type(a.nr_seq_episodio, null, a.nr_atendimento, a.ie_tipo_atendimento) = '1' order by	CASE WHEN coalesce(b.nr_seq_motivo::text, '') = '' THEN  0  ELSE 1 END ,
	b.nr_sequencia;


BEGIN
select	somente_numero(a.nr_seq_documento)
into STRICT	nr_seq_episodio_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia = nr_seq_fila_p;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);
reg_integracao_w.ie_marca_verif			:= 'X';

begin
open C01;
loop
fetch C01 into	
	nr_seq_motivo_w,
	dt_cancelamento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */	
end loop;
close C01;
exception
when others then
	dt_cancelamento_w	:=	null;
end;

reg_integracao_w.nm_elemento		:= 'CancelUserDate';

if (dt_cancelamento_w IS NOT NULL AND dt_cancelamento_w::text <> '') then
	begin
	reg_integracao_w.nm_tabela 	:= 'ATENDIMENTO_CANCELAMENTO';
	intpd_processar_atrib_envio(reg_integracao_w, 'NR_SEQ_MOTIVO', 'cancelreason', 'N', nr_seq_motivo_w, 'S', r_cancel_user_date_w.cancelreason);
	intpd_processar_atrib_envio(reg_integracao_w, 'NR_SEQ_MOTIVO', 'cancelreasonx', 'N', 'X', 'N', r_cancel_user_date_w.cancelreasonx);
	intpd_processar_atrib_envio(reg_integracao_w, 'DT_CANCELAMENTO', 'canceldate', 'N', dt_cancelamento_w, 'N', dt_cancelamento_w);
	r_cancel_user_date_w.canceldate		:= to_char(dt_cancelamento_w,'yyyy-mm-dd');
	intpd_processar_atrib_envio(reg_integracao_w, 'DT_CANCELAMENTO', 'canceldatex', 'N', 'X', 'N', r_cancel_user_date_w.canceldatex);
	intpd_processar_atrib_envio(reg_integracao_w, 'NM_USUARIO', 'canceluser', 'N', coalesce(ish_param_pck.get_nm_usuario,current_setting('ish_pat_case_pck.usernameish')::varchar(15)), 'N', r_cancel_user_date_w.canceluser);
	intpd_processar_atrib_envio(reg_integracao_w, 'NM_USUARIO', 'canceluserx', 'N', 'X', 'N', r_cancel_user_date_w.canceluserx);
	end;
end if;

RETURN NEXT r_cancel_user_date_w;
CALL intpd_gravar_log_fila(reg_integracao_w);
END;

$body$
