CREATE OR REPLACE FUNCTION ish_pat_case_pck.get_cancel_user_date_transf ( nr_seq_fila_p bigint) RETURNS SETOF T_CANCEL_USER_DATE_TRANSF AS $body$
DECLARE

		
r_cancel_user_date_transf_w	r_cancel_user_date_transf;

nr_seq_documento_w	intpd_fila_transmissao.nr_seq_documento%type;
nr_seq_regra_w		intpd_eventos_sistema.nr_seq_regra_conv%type;
reg_integracao_w	gerar_int_padrao.reg_integracao_conv;
dt_geral_w		varchar(255);
dt_atualizacao_w	timestamp;


BEGIN
select	a.dt_atualizacao,
	current_setting('ish_pat_case_pck.usernameish')::varchar(15),
	a.nr_seq_documento
into STRICT	dt_atualizacao_w,
	r_cancel_user_date_transf_w.canceluser,
	nr_seq_documento_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia = nr_seq_fila_p;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);

reg_integracao_w.nm_tabela 			:= 'INTPD_FILA_TRANSMISSAO';
reg_integracao_w.nm_elemento			:= 'PatcaseCanceltransfer';

intpd_processar_atrib_envio(reg_integracao_w, 'DT_ATUALIZACAO', 'canceldate', 'N', dt_atualizacao_w, 'N', dt_geral_w);
r_cancel_user_date_transf_w.canceldate	:= to_char(to_date(dt_geral_w),'yyyy-mm-dd');
intpd_processar_atrib_envio(reg_integracao_w, 'DT_ATUALIZACAO','canceldatex', 'N', 'X', 'N', r_cancel_user_date_transf_w.canceldatex);
r_cancel_user_date_transf_w.canceldatex	:=	'X';

/*
cancelreason		varchar2(3)
cancelreasonx		varchar2(1)
*/
RETURN NEXT r_cancel_user_date_transf_w;
CALL intpd_gravar_log_fila(reg_integracao_w);
END;

$body$
