SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION ish_pat_case_pck.get_change_absence_data ( nr_seq_fila_p bigint) RETURNS SETOF T_CHANGE_ABSENCE_DATA AS $body$
DECLARE

		
r_change_absence_data_w	r_change_absence_data;

nr_seq_documento_w		intpd_fila_transmissao.nr_seq_documento%type;
ie_conversao_w			intpd_eventos_sistema.ie_conversao%type;
nr_seq_regra_w			intpd_eventos_sistema.nr_seq_regra_conv%type;

nr_atendimento_w		atendimento_paciente.nr_atendimento%type;
nr_seq_atepacu_w		atend_paciente_unidade.nr_seq_interno%type;

nr_seq_classif_w		classif_pessoa.nr_sequencia%type;
atepacu_ant_w			atend_paciente_unidade%rowtype;
nr_seq_agrup_ant_w		setor_atendimento.nr_seq_agrupamento%type;
nr_seq_agrupamento_w	setor_atendimento.nr_seq_agrupamento%type;
reg_integracao_w		gerar_int_padrao.reg_integracao_conv;

cd_setor_w				varchar(255);
nm_leito_integracao_w	varchar(255);
cd_setor_atendimento_w	varchar(255);
dt_geral_w			timestamp;

c03 CURSOR FOR
SELECT	a.nr_seq_interno,
	a.dt_saida_temporaria,
	a.dt_retorno_saida_temporaria,
	a.cd_setor_atendimento,
	a.nr_seq_unid_ant
from	atend_paciente_unidade	a,
	atendimento_paciente	b
where	a.nr_atendimento		= b.nr_atendimento
and	a.nr_seq_interno		= nr_seq_documento_w;

c03_w	c03%rowtype;


BEGIN
select	a.nr_seq_documento,
	coalesce(b.ie_conversao,'I'),
	b.nr_seq_regra_conv
into STRICT	nr_seq_documento_w,
	ie_conversao_w,	
	nr_seq_regra_w
from	intpd_fila_transmissao	a,
	intpd_eventos_sistema	b
where	a.nr_seq_evento_sistema	= b.nr_sequencia
and	a.nr_sequencia			= nr_seq_fila_p;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);
reg_integracao_w.ie_somente_alterados	:=	'S';

open c03;
loop
fetch c03 into	
	c03_w;
EXIT WHEN NOT FOUND; /* apply on c03 */
	begin
	reg_integracao_w.nm_tabela	:=	'ATEND_PACIENTE_UNIDADE';
	reg_integracao_w.nm_elemento	:=	'InpatAbsenceData';

	begin
	select	*
	into STRICT	atepacu_ant_w
	from	atend_paciente_unidade
	where	nr_seq_interno = c03_w.nr_seq_unid_ant;
	
	select	a.nr_seq_agrupamento		
	into STRICT	nr_seq_agrup_ant_w		
	from	setor_atendimento a		
	where	a.cd_setor_atendimento = atepacu_ant_w.cd_setor_atendimento;
	
	exception
	when others then
		atepacu_ant_w.nr_seq_interno	:=	null;
	end;
	
	intpd_processar_atrib_envio(reg_integracao_w, 'DT_SAIDA_TEMPORARIA','MovemntDateBegx', 'S', c03_w.dt_saida_temporaria, 'N',r_change_absence_data_w.MovemntDateBegx);
	intpd_processar_atrib_envio(reg_integracao_w, 'DT_SAIDA_TEMPORARIA','MovemntDateBeg', 'N', c03_w.dt_saida_temporaria, 'N',dt_geral_w);
	r_change_absence_data_w.MovemntDateBeg	:= to_char(dt_geral_w,'YYYY-MM-DD');
	
	intpd_processar_atrib_envio(reg_integracao_w, 'DT_SAIDA_TEMPORARIA','MovemntTimeBegx', 'S', c03_w.dt_saida_temporaria, 'N',r_change_absence_data_w.MovemntTimeBegx);
	intpd_processar_atrib_envio(reg_integracao_w, 'DT_SAIDA_TEMPORARIA','MovemntTimeBeg', 'N', c03_w.dt_saida_temporaria, 'N', dt_geral_w);
	r_change_absence_data_w.MovemntTimeBeg := to_char(dt_geral_w,'hh24:mi:ss');

	if (r_change_absence_data_w.MovemntDateBegx = 'X') then
		r_change_absence_data_w.MovemntTypeBeg 	:= 'AB';
		r_change_absence_data_w.MovemntTypeBegx	:= 'X';
	end if;
	
	intpd_processar_atrib_envio(reg_integracao_w, 'DT_RETORNO_SAIDA_TEMPORARIA','MovemntDateEndx', 'S', c03_w.dt_saida_temporaria, 'N',r_change_absence_data_w.MovemntDateEndx);
	intpd_processar_atrib_envio(reg_integracao_w, 'DT_RETORNO_SAIDA_TEMPORARIA','MovemntDateEnd', 'N', c03_w.dt_retorno_saida_temporaria, 'N', dt_geral_w);
	r_change_absence_data_w.MovemntDateEnd	:= to_char(dt_geral_w,'YYYY-MM-DD');
	
	intpd_processar_atrib_envio(reg_integracao_w, 'DT_RETORNO_SAIDA_TEMPORARIA','MovemntTimeEndx', 'S', c03_w.dt_saida_temporaria, 'N',r_change_absence_data_w.MovemntTimeEndx);
	intpd_processar_atrib_envio(reg_integracao_w, 'DT_RETORNO_SAIDA_TEMPORARIA','MovemntTimeEnd', 'N', c03_w.dt_retorno_saida_temporaria, 'N', dt_geral_w);
	r_change_absence_data_w.MovemntTimeEnd 	:= to_char(dt_geral_w,'hh24:mi:ss');
	
	if (r_change_absence_data_w.MovemntDateEndx = 'X') then
		r_change_absence_data_w.MovemntTypeEnd 	:= 'AE';
		r_change_absence_data_w.MovemntTypeEndx	:= 'X';
		
		--Para trocar de P para null
		r_change_absence_data_w.StatusIndEndx	:= 'X';
		r_change_absence_data_w.StatusIndEnd	:= null;
	end if;
	
	intpd_processar_atrib_envio(reg_integracao_w, 'NR_SEQ_INTERNO','extmovementid', 'N', c03_w.nr_seq_interno, 'N', r_change_absence_data_w.extmovementid);
	intpd_processar_atrib_envio(reg_integracao_w, 'NR_SEQ_INTERNO','extmovementidx', 'S', c03_w.nr_seq_interno, 'N', r_change_absence_data_w.extmovementidx);
	
	exit;
	end;
end loop;
close c03;

RETURN NEXT r_change_absence_data_w;
CALL intpd_gravar_log_fila(reg_integracao_w);
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
