/*-----------------------------------------------------------------------------------------------------------------------------------------------------------------
'PatcaseChangetransfer'
*/
CREATE OR REPLACE FUNCTION ish_pat_case_pck.get_change_transfer ( nr_seq_fila_p bigint) RETURNS SETOF T_CHANGE_TRANSFER AS $body$
DECLARE

		
r_change_transfer_w	r_change_transfer;

nr_seq_documento_w	intpd_fila_transmissao.nr_seq_documento%type;
ie_conversao_w		intpd_eventos_sistema.ie_conversao%type;
nr_seq_regra_w		intpd_eventos_sistema.nr_seq_regra_conv%type;

nr_atendimento_w		atendimento_paciente.nr_atendimento%type;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
cd_empresa_w			empresa.cd_empresa%type;
cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;
atendimento_paciente_mv_w	atendimento_paciente%rowtype;
atend_paciente_unidade_mv_w 	atend_paciente_unidade%rowtype;
unidade_atendimento_mv_w	unidade_atendimento%rowtype;
episodio_paciente_mv_w 		episodio_paciente%rowtype;
cur_ferida_tr_w 		cur_ferida%rowtype;
reg_integracao_w		gerar_int_padrao.reg_integracao_conv;
nr_episodio_w			episodio_paciente.nr_episodio%type;
nr_seq_interno_w		intpd_fila_transmissao.nr_seq_documento%type;

ds_seqno_w				varchar(255);


BEGIN
select	a.nr_seq_documento,
	coalesce(b.ie_conversao,'I'),
	b.nr_seq_regra_conv
into STRICT	nr_seq_documento_w,
	ie_conversao_w,	
	nr_seq_regra_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia = nr_seq_fila_p;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);
reg_integracao_w.ie_somente_alterados		:= 'N';
reg_integracao_w.nm_elemento			:= 'PatcaseChangetransfer';

begin
select	b.nr_episodio,
	a.cd_pessoa_fisica,
	a.cd_estabelecimento
into STRICT	nr_episodio_w,
	cd_pessoa_fisica_w,
	cd_estabelecimento_w
FROM atend_paciente_unidade c, atendimento_paciente a
LEFT OUTER JOIN episodio_paciente b ON (a.nr_seq_episodio = b.nr_sequencia)
WHERE c.nr_atendimento  = a.nr_atendimento  and c.nr_seq_interno = nr_seq_documento_w;

reg_integracao_w.nm_tabela 	:= 'EPISODIO_PACIENTE';
intpd_processar_atrib_envio(reg_integracao_w, 'NR_EPISODIO', 'patcaseid', 'N', nr_episodio_w, 'N', r_change_transfer_w.patcaseid);

select	cd_empresa
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento = cd_estabelecimento_w;

ds_seqno_w			:=	intpd_conv('ATEND_PACIENTE_UNIDADE','NR_SEQ_INTERNO', nr_seq_documento_w, nr_seq_regra_w, ie_conversao_w, 'E');
reg_integracao_w.nm_tabela 	:= 'ATEND_PACIENTE_UNIDADE';
intpd_processar_atrib_envio(reg_integracao_w, 'NR_SEQ_INTERNO', 'movemntseqno', 'N', substr(ds_seqno_w,position(current_setting('ish_pat_case_pck.ds_separador_w')::varchar(10) in ds_seqno_w) + 1, 255), 'N', r_change_transfer_w.movemntseqno);

reg_integracao_w.nm_tabela 	:= 'ESTABELECIMENTO';
intpd_processar_atrib_envio(reg_integracao_w, 'CD_EMPRESA', 'client', 'N', cd_empresa_w, 'S', r_change_transfer_w.client);

reg_integracao_w.nm_tabela 	:= 'ATENDIMENTO_PACIENTE';
intpd_processar_atrib_envio(reg_integracao_w, 'CD_ESTABELECIMENTO', 'institution', 'N', cd_estabelecimento_w, 'S', r_change_transfer_w.institution);

exception
when others then
	null;
end;

RETURN NEXT r_change_transfer_w;
CALL intpd_gravar_log_fila(reg_integracao_w);
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
