SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION ish_pat_case_pck.get_detail_request ( nr_seq_fila_p bigint) RETURNS SETOF T_GET_DETAIL AS $body$
DECLARE


r_get_detail_w		r_get_detail;

cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;	

ie_conversao_w		intpd_eventos_sistema.ie_conversao%type;
nr_seq_regra_w		intpd_eventos_sistema.nr_seq_regra_conv%type;
reg_integracao_w	gerar_int_padrao.reg_integracao_conv;
institute_w		hcm_fall.institute%type;
movenumber_w		hcm_fall.movenumber%type;
caseno_w		hcm_fall.caseno%type;
patientno_w		hcm_patienten.patientno%type;
sendclient_w		hcm_kopf.sendclient%type;
nr_seq_mensagem_w	hcm_segmento.nr_seq_mensagem%type;
nr_seq_documento_w	intpd_fila_transmissao.nr_seq_documento%type;
ie_geracao_w		intpd_fila_transmissao.ie_geracao%type;


BEGIN

CALL wheb_usuario_pck.set_nm_usuario(current_setting('ish_pat_case_pck.usernametasy')::varchar(15));

begin
select	a.nr_seq_documento,
	coalesce(b.ie_conversao,'I'),
	b.nr_seq_regra_conv,
	a.ie_geracao
into STRICT	nr_seq_documento_w,
	ie_conversao_w,	
	nr_seq_regra_w,
	ie_geracao_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia = nr_seq_fila_p;
exception
when others then
	nr_seq_documento_w	:=	null;
end;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);

reg_integracao_w.nm_elemento			:= 'PatcaseGetdetail';

if (ie_geracao_w = 'U') then
	ish_get_manual_values(nr_seq_fila_p, patientno_w, caseno_w, movenumber_w, sendclient_w, institute_w);
else
	begin
	if (nr_seq_documento_w IS NOT NULL AND nr_seq_documento_w::text <> '') then
		begin
		begin
		select	a.institute,
			a.caseno,
			b.nr_seq_mensagem			
		into STRICT	institute_w,
			caseno_w,
			nr_seq_mensagem_w			
		from	hcm_fall a,
			hcm_segmento b
		where	a.nr_seq_segmento = b.nr_sequencia
		and	a.nr_sequencia = nr_seq_documento_w;
		
		select	a.sendclient
		into STRICT	sendclient_w
		from	hcm_kopf a,
			hcm_segmento b
		where	a.nr_seq_segmento = b.nr_sequencia
		and	b.nr_seq_mensagem = nr_seq_mensagem_w;
		exception
		when others then
			null;
		end;			
		end;
	end if;
	end;
end if;

intpd_processar_atrib_envio(reg_integracao_w, 'INSTITUTE', 'institution', 'N', institute_w, 'N', r_get_detail_w.institution);
intpd_processar_atrib_envio(reg_integracao_w, 'SENDCLIENT', 'client', 'N', sendclient_w, 'N', r_get_detail_w.client);
intpd_processar_atrib_envio(reg_integracao_w, 'CASENO', 'patcaseid', 'N', caseno_w, 'N', r_get_detail_w.patcaseid);

RETURN NEXT r_get_detail_w;
CALL intpd_gravar_log_fila(reg_integracao_w);
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
