-- request
CREATE OR REPLACE FUNCTION ish_pat_case_pck.get_discharge_request (nr_seq_fila_p bigint) RETURNS SETOF T_GET_DISCHARGE AS $body$
DECLARE


r_get_discharge_w	r_get_discharge;
nr_seq_hcm_fall_w	hcm_patienten.nr_sequencia%type;
nr_seq_documento_w	intpd_fila_transmissao.nr_seq_documento%type;
ie_geracao_w		intpd_fila_transmissao.ie_geracao%type;
patientno_w		hcm_patienten.patientno%type;
sendclient_w		hcm_kopf.sendclient%type;


BEGIN

begin
	select	nr_seq_documento,
		ie_geracao
	into STRICT	nr_seq_documento_w,
		ie_geracao_w
	from	intpd_fila_transmissao
	where	nr_sequencia = nr_seq_fila_p;
exception
	when 	others then
		nr_seq_documento_w := null;
end;

if (ie_geracao_w = 'U') then
	ish_get_manual_values(nr_seq_fila_p, patientno_w, r_get_discharge_w.patcaseid, r_get_discharge_w.movemntseqno, sendclient_w, r_get_discharge_w.institution);
else
	begin
	if (nr_seq_documento_w IS NOT NULL AND nr_seq_documento_w::text <> '') then
		begin
		select	institute,
			movenumber,
			caseno
		into STRICT	r_get_discharge_w.institution,
			r_get_discharge_w.movemntseqno,
			r_get_discharge_w.patcaseid
		from	hcm_fall
		where	nr_sequencia = nr_seq_documento_w;
		end;
	end if;
	end;
end if;

RETURN NEXT r_get_discharge_w;
END;

$body$
