SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION ish_pat_case_pck.get_movement_list_request ( nr_seq_fila_p bigint) RETURNS SETOF T_GET_MOVEMENT_LIST AS $body$
DECLARE

		
r_get_movement_list_w	r_get_movement_list;

cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
ie_conversao_w		intpd_eventos_sistema.ie_conversao%type;
nr_seq_regra_w		intpd_eventos_sistema.nr_seq_regra_conv%type;
reg_integracao_w	gerar_int_padrao.reg_integracao_conv;
institute_w		hcm_fall.institute%type;
movenumber_w		hcm_fall.movenumber%type;
caseno_w		hcm_fall.caseno%type;
caseno_ww		intpd_fila_transmissao.nr_seq_documento%type;
patientno_w		hcm_patienten.patientno%type;
cd_empresa_w		estabelecimento.cd_empresa%type;
nr_seq_documento_w	intpd_fila_transmissao.nr_seq_documento%type;
ie_geracao_w		intpd_fila_transmissao.ie_geracao%type;


BEGIN
begin
select	a.nr_seq_documento,
	coalesce(b.ie_conversao,'I'),
	b.nr_seq_regra_conv,
	a.ie_geracao
into STRICT	nr_seq_documento_w,
	ie_conversao_w,	
	nr_seq_regra_w,
	ie_geracao_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia = nr_seq_fila_p;
exception
when others then
	nr_seq_documento_w	:=	null;
end;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);
reg_integracao_w.nm_elemento			:= 'PatcaseGetmovementlist';

if (ie_geracao_w = 'U') then
	ish_get_manual_values(nr_seq_fila_p, patientno_w, caseno_ww, movenumber_w, cd_empresa_w, institute_w);
	
	if (position('-' in caseno_ww) > 0) then
		caseno_w	:=	substr(obter_valor_campo_separador(caseno_ww, 1, '-'),1 , 10);
	else
		caseno_w	:=	caseno_ww;
	end if;
else
	begin
	if (nr_seq_documento_w IS NOT NULL AND nr_seq_documento_w::text <> '') then
		begin
		select	institute,
			movenumber,
			caseno
		into STRICT	institute_w,
			movenumber_w,
			caseno_w
		from	hcm_fall
		where	nr_sequencia = nr_seq_documento_w;

		reg_integracao_w.nm_tabela 	:= 'HCM_FALL';
				
		cd_estabelecimento_w	:=	 intpd_conv('ESTABELECIMENTO', 'CD_ESTABELECIMENTO', institute_w, nr_seq_regra_w, ie_conversao_w, 'I');
		reg_integracao_w.nm_tabela 	:= 'ESTABELECIMENTO';
		
		begin
		select	cd_empresa
		into STRICT	cd_empresa_w
		from	estabelecimento
		where	cd_estabelecimento = cd_estabelecimento_w;
		exception
		when others then
			r_get_movement_list_w.client	:=	null;
		end;
		
		r_get_movement_list_w.CancelledIncl	:=	'X';
		end;
	end if;
	end;
end if;

if (ie_geracao_w = 'U') then
	intpd_processar_atrib_envio(reg_integracao_w, 'CD_EMPRESA', 'client', 'N', cd_empresa_w, 'N', r_get_movement_list_w.client);
else
	intpd_processar_atrib_envio(reg_integracao_w, 'CD_EMPRESA', 'client', 'N', cd_empresa_w, 'S', r_get_movement_list_w.client);
end if;

intpd_processar_atrib_envio(reg_integracao_w, 'INSTITUTE', 'institution', 'N', institute_w, 'N', r_get_movement_list_w.institution);
intpd_processar_atrib_envio(reg_integracao_w, 'MOVENUMBER', 'movemntseqno', 'N', movenumber_w, 'N', r_get_movement_list_w.movemntseqno);
intpd_processar_atrib_envio(reg_integracao_w, 'CASENO', 'patcaseid', 'N', caseno_w, 'N', r_get_movement_list_w.patcaseid);
		
RETURN NEXT r_get_movement_list_w;
CALL intpd_gravar_log_fila(reg_integracao_w);

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON FUNCTION ish_pat_case_pck.get_movement_list_request ( nr_seq_fila_p bigint) FROM PUBLIC;
