SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION ish_pat_case_pck.get_nffzdata ( nr_seq_fila_p bigint) RETURNS SETOF T_NFFZDATA AS $body$
DECLARE


r_assignment_w			r_assignment;
r_nffzData_w			r_nffzData;
reg_integracao_w		gerar_int_padrao.reg_integracao_conv;
nr_seq_documento_w		intpd_fila_transmissao.nr_seq_documento%type;
ie_operacao_w			intpd_fila_transmissao.ie_operacao%type;
episodio_acompanhante_w		episodio_acompanhante%rowtype;
nr_seq_ep_paciente_w		episodio_acompanhante.nr_seq_ep_paciente%type;
nr_seq_ep_acompanhante_w	episodio_acompanhante.nr_seq_ep_acompanhante%type;
dt_geral_w			varchar(255);


BEGIN
begin
select	a.nr_seq_documento,
	CASE WHEN a.ie_operacao='A' THEN  'U' WHEN a.ie_operacao='E' THEN  'D'  ELSE 'I' END
into STRICT	nr_seq_documento_w,
	ie_operacao_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia = nr_seq_fila_p;
exception
when others then
	nr_seq_documento_w	:=	null;
end;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);
reg_integracao_w.nm_elemento	:= '_-rzvish_-assignment';

nr_seq_ep_paciente_w		:=	somente_numero(obter_valor_campo_separador(nr_seq_documento_w, 1, '|'));
nr_seq_ep_acompanhante_w	:=	somente_numero(obter_valor_campo_separador(nr_seq_documento_w, 2, '|'));

begin
select	*
into STRICT	episodio_acompanhante_w
from	episodio_acompanhante
where	nr_seq_ep_paciente = nr_seq_ep_paciente_w
and	nr_seq_ep_acompanhante = nr_seq_ep_acompanhante_w  LIMIT 1;
exception
when others then
	episodio_acompanhante_w	:=	null;
end;

select	client,
	institution
into STRICT	r_assignment_w.client,
	r_assignment_w.institution
from	table(ish_pat_case_pck.get_assignment(nr_seq_fila_p));

reg_integracao_w.nm_tabela	:= 'ESTABELECIMENTO';
intpd_processar_atrib_envio(reg_integracao_w, 'CD_EMPRESA', 'client', 'N', r_assignment_w.client, 'N', r_nffzData_w.Mandt);
intpd_processar_atrib_envio(reg_integracao_w, 'CD_ESTABELECIMENTO', 'institution', 'N', r_assignment_w.institution, 'N', r_nffzData_w.institution);

intpd_processar_atrib_envio(reg_integracao_w, 'DT_ATUALIZACAO_NRE', 'begdt', 'N', episodio_acompanhante_w.dt_atualizacao, 'N', dt_geral_w);
r_nffzData_w.Begdt		:=	to_char(to_date(dt_geral_w),'YYYY-MM-DD');
intpd_processar_atrib_envio(reg_integracao_w, 'REFA1', 'refa1', 'N', 'P', 'N', r_nffzData_w.refa1);
intpd_processar_atrib_envio(reg_integracao_w, 'REFA2', 'refa2', 'N', 'B', 'N', r_nffzData_w.refa2);
intpd_processar_atrib_envio(reg_integracao_w, 'IE_OPERACAO', 'kz', 'N', ie_operacao_w, 'N', r_nffzData_w.kz);
RETURN NEXT r_nffzData_w;

CALL intpd_gravar_log_fila(reg_integracao_w);
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
