CREATE OR REPLACE FUNCTION ish_pat_case_pck.get_patcase_change_inpat_adm ( nr_seq_fila_p bigint) RETURNS SETOF T_PATCASE_CHANGE_INPAT_ADMISS AS $body$
DECLARE

		
r_patcase_change_inpat_adm_w	r_patcase_change_inpat_admiss;

nr_seq_documento_w	intpd_fila_transmissao.nr_seq_documento%type;
ie_conversao_w		intpd_eventos_sistema.ie_conversao%type;
nr_seq_regra_w		intpd_eventos_sistema.nr_seq_regra_conv%type;

nr_atendimento_w		atendimento_paciente.nr_atendimento%type;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
cd_empresa_w		empresa.cd_empresa%type;
reg_integracao_w	gerar_int_padrao.reg_integracao_conv;


BEGIN
select	a.nr_seq_documento,
	coalesce(b.ie_conversao,'I'),
	b.nr_seq_regra_conv
into STRICT	nr_seq_documento_w,
	ie_conversao_w,	
	nr_seq_regra_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia = nr_seq_fila_p;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);
reg_integracao_w.ie_somente_alterados		:= 'S';

begin
select	nr_episodio
into STRICT	r_patcase_change_inpat_adm_w.patcaseid
from	episodio_paciente
where	nr_sequencia = nr_seq_documento_w;

nr_atendimento_w := ish_get_encounter_case(coalesce(r_patcase_change_inpat_adm_w.patcaseid, nr_seq_documento_w), 'INPAT');

select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	atendimento_paciente
where	nr_atendimento = nr_atendimento_w;

select	cd_empresa
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento = cd_estabelecimento_w;

reg_integracao_w.nm_tabela 		:= 'ESTABELECIMENTO';
reg_integracao_w.nm_elemento		:= 'PatcaseChangeinpatadmiss';
intpd_processar_atrib_envio(reg_integracao_w, 'CD_EMPRESA', 'client', 'N', cd_empresa_w, 'S', r_patcase_change_inpat_adm_w.client);
intpd_processar_atrib_envio(reg_integracao_w, 'CD_ESTABELECIMENTO', 'institution', 'N', cd_estabelecimento_w, 'S', r_patcase_change_inpat_adm_w.institution);

exception
when others then
	null;
end;

--r_patcase_change_inpat_adm_w.testrun		:=	;
RETURN NEXT r_patcase_change_inpat_adm_w;
CALL intpd_gravar_log_fila(reg_integracao_w);
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
