/*-----------------------------------------------------------------------------------------------------------------------------------------------------------------
'PatcaseAddinpatadmiss'
*/
CREATE OR REPLACE FUNCTION ish_pat_case_pck.get_patcase_inpat_admiss ( nr_seq_fila_p bigint) RETURNS SETOF T_PATCASE_INPAT_ADMISS AS $body$
DECLARE


r_patcase_inpat_admiss_w	r_patcase_inpat_admiss;

nr_seq_documento_w	intpd_fila_transmissao.nr_seq_documento%type;
ie_conversao_w		intpd_eventos_sistema.ie_conversao%type;
nr_seq_regra_w		intpd_eventos_sistema.nr_seq_regra_conv%type;

nr_atendimento_w	atendimento_paciente.nr_atendimento%type;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
cd_empresa_w		empresa.cd_empresa%type;
cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;
reg_integracao_w	gerar_int_padrao.reg_integracao_conv;

nr_episodio_w			episodio_paciente.nr_episodio%type;
cd_pessoa_fisica_externo_w	pf_codigo_externo.cd_pessoa_fisica_externo%type;
ie_evento_w		intpd_fila_transmissao.ie_evento%type;


BEGIN
select	a.nr_seq_documento,
	coalesce(b.ie_conversao,'I'),
	b.nr_seq_regra_conv,
	a.ie_evento
into STRICT	nr_seq_documento_w,
	ie_conversao_w,	
	nr_seq_regra_w,
	ie_evento_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia = nr_seq_fila_p;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);

begin
select	nr_episodio,
	cd_pessoa_fisica
into STRICT	nr_episodio_w,
	cd_pessoa_fisica_w
from	episodio_paciente
where	nr_sequencia = nr_seq_documento_w;

if (ie_evento_w = '220') then
	nr_atendimento_w	:=	ish_get_encounter_case(coalesce(nr_episodio_w, nr_seq_documento_w), null); --Null pq quanto e troca do tipo do case, o atendimento inpatient ainda nao foi criado.
else
	nr_atendimento_w	:=	ish_get_encounter_case(coalesce(nr_episodio_w, nr_seq_documento_w), 'INPAT');
end if;

if (nr_episodio_w IS NOT NULL AND nr_episodio_w::text <> '') then
	reg_integracao_w.nm_tabela 	:= 'EPISODIO_PACIENTE';
	intpd_processar_atrib_envio(reg_integracao_w, 'NR_EPISODIO', 'patcaseid', 'N', nr_episodio_w, 'N', r_patcase_inpat_admiss_w.patcaseid);
	r_patcase_inpat_admiss_w.transactmode	:=	'S';
else	
	r_patcase_inpat_admiss_w.transactmode	:=	'N';
end if;

reg_integracao_w.nm_elemento	:= 'PatcaseAddinpatadmiss';

select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	atendimento_paciente
where	nr_atendimento = nr_atendimento_w;

select	cd_empresa
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento = cd_estabelecimento_w;

reg_integracao_w.nm_tabela 	:= 'ESTABELECIMENTO';
intpd_processar_atrib_envio(reg_integracao_w, 'CD_EMPRESA', 'client', 'N', cd_empresa_w, 'S', r_patcase_inpat_admiss_w.client);
reg_integracao_w.nm_tabela 	:= 'ATENDIMENTO_PACIENTE';
intpd_processar_atrib_envio(reg_integracao_w, 'CD_ESTABELECIMENTO', 'institution', 'N', cd_estabelecimento_w, 'S', r_patcase_inpat_admiss_w.institution);


begin
select	cd_pessoa_fisica_externo
into STRICT	cd_pessoa_fisica_externo_w
from	pf_codigo_externo
where	ie_tipo_codigo_externo = 'ISH'
and	cd_pessoa_fisica = cd_pessoa_fisica_w  LIMIT 1;
exception
when others then
	null;
end;
exception
when others then
	null;
end;

reg_integracao_w.nm_tabela 	:= 'PF_CODIGO_EXTERNO';
intpd_processar_atrib_envio(reg_integracao_w, 'CD_PESSOA_FISICA_EXTERNO', 'patientid', 'N', cd_pessoa_fisica_externo_w, 'N', r_patcase_inpat_admiss_w.patientid);

--r_patcase_inpat_admiss_w.testrun		:=	;
RETURN NEXT r_patcase_inpat_admiss_w;
CALL intpd_gravar_log_fila(reg_integracao_w);
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON FUNCTION ish_pat_case_pck.get_patcase_inpat_admiss ( nr_seq_fila_p bigint) FROM PUBLIC;
