SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE ish_pat_case_pck.get_rec_check_response ( nr_sequencia_p bigint) AS $body$
DECLARE


xml_w				xml;
ds_xml_w			text;
ie_status_w			intpd_fila_transmissao.ie_status%type		:=	'S';
ie_tipo_erro_w		intpd_fila_transmissao.ie_tipo_erro%type	:=	'F';
nr_seq_documento_w	intpd_fila_transmissao.nr_seq_documento%type;
reg_integracao_w	gerar_int_padrao.reg_integracao_conv;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	intpd_fila_transmissao
	where	ie_evento in ('106','120')
	and	ie_status	= 'E'
	and	ie_tipo_erro	= 'F'
	and	nr_seq_documento = nr_seq_documento_w;

c01_w	c01%rowtype;	
	

BEGIN
CALL intpd_inicializacao(nr_sequencia_p);
intpd_reg_integracao_inicio(nr_sequencia_p, 'R', reg_integracao_w);

delete	FROM intpd_log_recebimento
where	nr_seq_fila = nr_sequencia_p;

begin
select	a.ds_xml_retorno,
	a.nr_seq_documento
into STRICT	ds_xml_w,
	nr_seq_documento_w
from	intpd_fila_transmissao a
where	a.nr_sequencia = nr_sequencia_p;
exception
when others then
	null;
end;

ish_converter_response(nr_sequencia_p, ds_xml_w, ie_status_w, ie_tipo_erro_w, xml_w);

if (ie_status_w = 'E') then
	update	intpd_fila_transmissao
	set	ie_status = ie_status_w,
		ie_tipo_erro = ie_tipo_erro_w,
		ie_response_procedure = 'S'
	where	nr_sequencia = nr_sequencia_p;
else
	update	intpd_fila_transmissao
	set	ie_status = 'S',
		ie_response_procedure = 'S'
	where	nr_sequencia = nr_sequencia_p;
	
	open c01;
	loop
	fetch c01 into	
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		update	intpd_fila_transmissao
		set	ie_status	= 'P',
			ie_tipo_erro	 = NULL,
			ds_log		 = NULL,
			ds_xml		 = NULL,
			ds_xml_retorno	 = NULL,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia = c01_w.nr_sequencia;
		
		delete	FROM intpd_log_recebimento
		where	nr_seq_fila = c01_w.nr_sequencia;
		end;
	end loop;
	close c01;	
end if;
commit;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
