CREATE OR REPLACE FUNCTION ish_pat_case_pck.get_record_check_data ( nr_seq_fila_p bigint) RETURNS SETOF T_RECORD_CHECK_DELETE AS $body$
DECLARE


r_record_check_delete_w		r_record_check_delete;

nr_seq_documento_w		intpd_fila_transmissao.nr_seq_documento%type;
ie_conversao_w			intpd_eventos_sistema.ie_conversao%type;
nr_seq_regra_w			intpd_eventos_sistema.nr_seq_regra_conv%type;

cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
cd_empresa_w			empresa.cd_empresa%type;
reg_integracao_w		gerar_int_padrao.reg_integracao_conv;
nr_episodio_w			episodio_paciente.nr_episodio%type;


BEGIN
select	a.nr_seq_documento,
	coalesce(b.ie_conversao,'I'),
	b.nr_seq_regra_conv
into STRICT	nr_seq_documento_w,
	ie_conversao_w,	
	nr_seq_regra_w
from	intpd_fila_transmissao	a,
	intpd_eventos_sistema 	b
where	a.nr_seq_evento_sistema	= b.nr_sequencia
and	a.nr_sequencia 		= nr_seq_fila_p;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);

begin

begin
	select	a.nr_episodio,
			a.cd_estabelecimento
	into STRICT	nr_episodio_w,
			cd_estabelecimento_w
	from	episodio_paciente a
	where	a.nr_sequencia = nr_seq_documento_w;
exception
when no_data_found then
	null;
end;

if (coalesce(nr_episodio_w::text, '') = '') then
	select	max(b.nr_episodio),
			min(a.cd_estabelecimento)
	into STRICT	nr_episodio_w,
			cd_estabelecimento_w
	FROM atendimento_paciente a
LEFT OUTER JOIN episodio_paciente b ON (a.nr_seq_episodio = b.nr_sequencia)
WHERE a.nr_seq_episodio = nr_seq_documento_w;
end if;

reg_integracao_w.nm_elemento	:= '_-rzvish_-recordCheckDelete';
reg_integracao_w.nm_tabela 	:= 'EPISODIO_PACIENTE';
intpd_processar_atrib_envio(reg_integracao_w, 'NR_EPISODIO', 'patcaseid', 'N', nr_episodio_w, 'N', r_record_check_delete_w.patcaseid);

select	cd_empresa
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento	= cd_estabelecimento_w;

reg_integracao_w.nm_tabela	:= 'ESTABELECIMENTO';
intpd_processar_atrib_envio(reg_integracao_w, 'CD_EMPRESA', 'client', 'N', cd_empresa_w, 'S', r_record_check_delete_w.client);
intpd_processar_atrib_envio(reg_integracao_w, 'CD_ESTABELECIMENTO', 'institution', 'N', cd_estabelecimento_w, 'S', r_record_check_delete_w.institution);
r_record_check_delete_w.ie_mode := 'D';

exception
when others then
	null;
end;

RETURN NEXT r_record_check_delete_w;
CALL intpd_gravar_log_fila(reg_integracao_w);		
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
