CREATE OR REPLACE FUNCTION ish_pat_case_pck.get_transfer_request ( nr_seq_fila_p bigint) RETURNS SETOF T_GET_TRANSFER AS $body$
DECLARE

		
r_get_transfer_w	r_get_transfer;
nr_seq_hcm_fall_w	hcm_patienten.nr_sequencia%type;
reg_integracao_w	gerar_int_padrao.reg_integracao_conv;
institute_w		hcm_fall.institute%type;
movenumber_w		hcm_fall.movenumber%type;
caseno_w		hcm_fall.caseno%type;
patientno_w		hcm_patienten.patientno%type;
sendclient_w		hcm_kopf.sendclient%type;
ie_geracao_w		intpd_fila_transmissao.ie_geracao%type;


BEGIN
begin
select	nr_seq_documento,
	ie_geracao
into STRICT	nr_seq_hcm_fall_w,
	ie_geracao_w
from	intpd_fila_transmissao
where	nr_sequencia = nr_seq_fila_p;
exception
when others then
	nr_seq_hcm_fall_w	:=	null;
end;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);
reg_integracao_w.nm_elemento			:= 'PatcaseGettransfer';


if (ie_geracao_w = 'U') then
	ish_get_manual_values(nr_seq_fila_p, patientno_w, caseno_w, movenumber_w, sendclient_w, institute_w);
else
	begin
	if (nr_seq_hcm_fall_w IS NOT NULL AND nr_seq_hcm_fall_w::text <> '') then
		begin
		select	institute,
			movenumber,
			caseno
		into STRICT	institute_w,
			movenumber_w,
			caseno_w
		from	hcm_fall
		where	nr_sequencia = nr_seq_hcm_fall_w;
		
		reg_integracao_w.nm_tabela 	:= 'HCM_FALL';
		end;
	end if;
	end;
end if;

intpd_processar_atrib_envio(reg_integracao_w, 'INSTITUTE', 'institution', 'N', institute_w, 'N', r_get_transfer_w.institution);
intpd_processar_atrib_envio(reg_integracao_w, 'MOVENUMBER', 'movemntseqno', 'N', movenumber_w, 'N', r_get_transfer_w.movemntseqno);
intpd_processar_atrib_envio(reg_integracao_w, 'CASENO', 'patcaseid', 'N', caseno_w, 'N', r_get_transfer_w.patcaseid);

RETURN NEXT r_get_transfer_w;
CALL intpd_gravar_log_fila(reg_integracao_w);
END;

