SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE ish_pat_case_pck.inativar_pf_medico_ext_gp ( cd_paciente_p text, cd_medico_p text, nr_seq_tipo_medico_p bigint) AS $body$
DECLARE
	
ie_medico_familia_w	varchar(1);

C01 CURSOR FOR
	SELECT 	a.nr_sequencia
	from	pf_medico_externo a,
		tipo_medico_externo b
	where	a.nr_seq_tipo_medico = b.nr_sequencia
	and	b.IE_MEDICO_FAMILIA = 'S'
	and	a.cd_pessoa_fisica = cd_paciente_p
	and	a.cd_medico <> cd_medico_p
	and (coalesce(a.dt_fim_vigencia::text, '') = '' or (trunc(a.dt_fim_vigencia) > trunc(clock_timestamp())));

BEGIN

--OS2147902 - Tratamento para inativar os demais GPs
select	max(ie_medico_familia)
into STRICT	ie_medico_familia_w
from	tipo_medico_externo
where	nr_sequencia 	= nr_seq_tipo_medico_p;

if (ie_medico_familia_w	= 'S') then
	
	for r_c01 in c01 loop
		begin
		update	pf_medico_externo
		set	dt_fim_vigencia = clock_timestamp()
		where	nr_sequencia = r_c01.nr_sequencia;
		end;
	end loop;
end if;


END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
