CREATE OR REPLACE FUNCTION ish_pat_case_pck.obter_bsnr_medico_estab (cd_pessoa_fisica_p text, cd_especialidade_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

	
cd_cgc_w	estabelecimento.cd_cgc%type;
nr_bsnr_w	medico_espec_bsnr.nr_bsnr%type;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	max(cd_cgc)
	into STRICT	cd_cgc_w
	from	estabelecimento
	where	cd_estabelecimento	= cd_estabelecimento_p;

	begin --Busca por especialidade e CGC
	select	nr_bsnr
	into STRICT	nr_bsnr_w
	from	medico_espec_bsnr
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	cd_especialidade	= cd_especialidade_p
	and	cd_cgc			= cd_cgc_w  LIMIT 1;
	exception
	when others then
		begin --Busca por especialidade
		select	nr_bsnr
		into STRICT	nr_bsnr_w
		from	medico_espec_bsnr
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	cd_especialidade	= cd_especialidade_p  LIMIT 1;
		exception
		when others then
			begin --Busca somente pelo medico
			select	nr_bsnr
			into STRICT	nr_bsnr_w
			from	medico_espec_bsnr
			where	cd_pessoa_fisica	= cd_pessoa_fisica_p  LIMIT 1;
			exception
			when others then
				nr_bsnr_w	:= null;
			end;
		end;
	end;
end if;

return	nr_bsnr_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
