CREATE OR REPLACE PROCEDURE ish_pat_case_pck.registrar_movto ( ie_acao_p text, atend_paciente_unidade_p INOUT atend_paciente_unidade) AS $body$
DECLARE

		
qt_tentativa_w	integer	:=	0;	
ie_sucesso_w	varchar(1)	:=	'N';
ds_erro_w	varchar(4000);
		

BEGIN
while 	(ie_sucesso_w = 'N' AND qt_tentativa_w < 10) loop
	begin	
	if (ie_acao_p = 'I') then
		insert into atend_paciente_unidade values (atend_paciente_unidade_p.*);
	elsif (ie_acao_p = 'U') then
		update	atend_paciente_unidade
		set 	row = atend_paciente_unidade_p
		where	nr_seq_interno = atend_paciente_unidade_p.nr_seq_interno;		
	end if;
	
	ie_sucesso_w	:=	'S';
	exception
	when unique_violation then
		begin
		ds_erro_w	:=	substr(dbms_utility.format_error_backtrace || chr(13) || chr(10) || sqlerrm,1, 2000);		
		if (atend_paciente_unidade_p.dt_saida_unidade IS NOT NULL AND atend_paciente_unidade_p.dt_saida_unidade::text <> '') and (atend_paciente_unidade_p.dt_saida_unidade = atend_paciente_unidade_p.dt_entrada_unidade) then
			atend_paciente_unidade_p.dt_entrada_unidade	:= atend_paciente_unidade_p.dt_entrada_unidade + 1/60/60/24;
			atend_paciente_unidade_p.dt_saida_unidade	:= atend_paciente_unidade_p.dt_entrada_unidade;
		else
			atend_paciente_unidade_p.dt_entrada_unidade	:= atend_paciente_unidade_p.dt_entrada_unidade + 1/60/60/24;
		end if;
		end;	
	when others then
		ds_erro_w	:=	substr(dbms_utility.format_error_backtrace || chr(13) || chr(10) || sqlerrm,1, 2000);		
		CALL wheb_mensagem_pck.exibir_mensagem_abort(ds_erro_w);
	end;
	
	qt_tentativa_w	:=	qt_tentativa_w + 1;
end loop;

if (ie_sucesso_w = 'N') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(ds_erro_w);
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
