SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION ish_patient_pck.get_addphones_change ( nr_seq_fila_p bigint, ie_tipo_p text) RETURNS SETOF T_ADDPHONES_CHANGE AS $body$
DECLARE


nr_seq_documento_w		intpd_fila_transmissao.nr_seq_documento%type;
ie_conversao_w			intpd_eventos_sistema.ie_conversao%type;
nr_seq_sistema_w		intpd_eventos_sistema.nr_seq_sistema%type;
nr_seq_projeto_xml_w		intpd_eventos_sistema.nr_seq_projeto_xml%type;
nr_seq_regra_w			intpd_eventos_sistema.nr_seq_regra_conv%type;
nr_seq_tipo_compl_adic_w	compl_pessoa_fisica.nr_seq_tipo_compl_adic%type;
r_addphones_change_w		r_addphones_change;
reg_integracao_w		gerar_int_padrao.reg_integracao_conv;
pessoa_fisica_empregador_w	pessoa_fisica_empregador%rowtype;

c01 CURSOR FOR
SELECT	cd_pessoa_fisica,
	nr_sequencia,
	ds_fone_adic
from	compl_pessoa_fisica
where	cd_pessoa_fisica = nr_seq_documento_w
and	ie_tipo_complemento <> 2;

c01_w	c01%rowtype;


BEGIN
select	a.nr_seq_documento,
	coalesce(b.ie_conversao,'I'),
	b.nr_seq_sistema,
	b.nr_seq_projeto_xml,
	b.nr_seq_regra_conv
into STRICT	nr_seq_documento_w,
	ie_conversao_w,
	nr_seq_sistema_w,
	nr_seq_projeto_xml_w,
	nr_seq_regra_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia = nr_seq_fila_p;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);
reg_integracao_w.ie_somente_alterados		:= 'N';

if (ie_tipo_p = 'I') then
	open c01;
	loop
	fetch c01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		r_addphones_change_w		:=	null;
		reg_integracao_w.nm_tabela 	:=	'COMPL_PESSOA_FISICA';		
		reg_integracao_w.nm_elemento	:=	'EAddPhones';
		intpd_processar_atrib_envio(reg_integracao_w, 'NR_SEQUENCIA','addrno', 'N', c01_w.nr_sequencia, 'N', r_addphones_change_w.addrno);
		intpd_processar_atrib_envio(reg_integracao_w, 'NR_SEQUENCIA','seqno', 'N', '001', 'N', r_addphones_change_w.seqno);
		intpd_processar_atrib_envio(reg_integracao_w, 'DS_FONE_ADIC','phoneno', 'N', c01_w.ds_fone_adic, 'N', r_addphones_change_w.phoneno);
		reg_integracao_w.ds_chave	:=	null;
		RETURN NEXT r_addphones_change_w;
		end;
	end loop;
	close c01;
	
	begin
	select	*
	into STRICT    pessoa_fisica_empregador_w
	from    pessoa_fisica_empregador
	where   nr_sequencia =	(SELECT	max(nr_sequencia)
				from	pessoa_fisica_empregador
				where	cd_pessoa_fisica = nr_seq_documento_w
				and	coalesce(dt_fim_trabalho::text, '') = '');

	reg_integracao_w.nm_tabela 		:= 'PESSOA_FISICA_EMPREGADOR';
	reg_integracao_w.ie_valida_chave	:= 'N';
	r_addphones_change_w			:= null;

	reg_integracao_w.ie_somente_alterados		:= 'N';
	intpd_processar_atrib_envio(reg_integracao_w, 'NR_SEQUENCIA','addrno', 'N', 99, 'N', r_addphones_change_w.addrno);
	intpd_processar_atrib_envio(reg_integracao_w, 'NR_SEQUENCIA','seqno', 'N', '001', 'N', r_addphones_change_w.seqno);
	reg_integracao_w.ie_somente_alterados		:= 'S';
	intpd_processar_atrib_envio(reg_integracao_w, 'NR_TELEFONE','phoneno', 'N', pessoa_fisica_empregador_w.nr_telefone, 'N', r_addphones_change_w.phoneno);
	RETURN NEXT r_addphones_change_w;
	exception
	when others then
		pessoa_fisica_empregador_w  := null;
	end;
	
end if;
CALL intpd_gravar_log_fila(reg_integracao_w);
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON FUNCTION ish_patient_pck.get_addphones_change ( nr_seq_fila_p bigint, ie_tipo_p text) FROM PUBLIC;
