CREATE OR REPLACE FUNCTION ish_patient_pck.get_detail_request ( nr_seq_fila_p bigint) RETURNS SETOF T_GET_DETAIL AS $body$
DECLARE


r_get_detail_w		r_get_detail;

nr_seq_hcm_patienten_w	hcm_patienten.nr_sequencia%type;
nr_seq_hcm_seg_pat_w	hcm_segmento.nr_sequencia%type;
nr_seq_mensagem_w	hcm_segmento.nr_seq_mensagem%type;
nr_seq_hcm_seg_hea_w	hcm_segmento.nr_sequencia%type;
caseno_w		hcm_fall.caseno%type;
movenumber_w		hcm_fall.movenumber%type;
sendclient_w		hcm_kopf.sendclient%type;
ie_geracao_w		intpd_fila_transmissao.ie_geracao%type;


BEGIN

begin
select	nr_seq_documento,
	ie_geracao
into STRICT	nr_seq_hcm_patienten_w,
	ie_geracao_w
from	intpd_fila_transmissao
where	nr_sequencia = nr_seq_fila_p;
exception
when others then
	nr_seq_hcm_patienten_w	:=	null;
end;

if (ie_geracao_w = 'U') then
	ish_get_manual_values(nr_seq_fila_p, r_get_detail_w.patientid, caseno_w, movenumber_w, sendclient_w, r_get_detail_w.institution);
else
	begin
	if (nr_seq_hcm_patienten_w IS NOT NULL AND nr_seq_hcm_patienten_w::text <> '') then
		begin
		select	nr_seq_segmento,
			patientno
		into STRICT	nr_seq_hcm_seg_pat_w,
			r_get_detail_w.patientid
		from	hcm_patienten
		where	nr_sequencia = nr_seq_hcm_patienten_w;

		begin
		select	nr_seq_mensagem
		into STRICT	nr_seq_mensagem_w
		from	hcm_segmento
		where	nr_sequencia = nr_seq_hcm_seg_pat_w;

		select	nr_sequencia
		into STRICT	nr_seq_hcm_seg_hea_w
		from	hcm_segmento
		where	nr_seq_mensagem = nr_seq_mensagem_w
		and	ie_tipo = 'HEA'  LIMIT 1;

		select	sendinstit,
			SENDCLIENT
		into STRICT	r_get_detail_w.institution,
			r_get_detail_w.client
		from	hcm_kopf
		where	nr_seq_segmento = nr_seq_hcm_seg_hea_w;
		exception
		when others then
			r_get_detail_w.institution	:=	null;
		end;
		end;
	end if;
	end;
end if;

RETURN NEXT r_get_detail_w;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
