'PatientEnqueue-PatientDequeue'
*/



CREATE OR REPLACE FUNCTION ish_patient_pck.get_enqueue_dequeue ( nr_seq_fila_p bigint) RETURNS SETOF T_GET_ENQUEUE_DEQUEUE AS $body$
DECLARE


r_get_enqueue_dequeue_w	r_get_enqueue_dequeue;

nr_seq_documento_w	intpd_fila_transmissao.nr_seq_documento%type;
ie_conversao_w		intpd_eventos_sistema.ie_conversao%type;
nr_seq_regra_w		intpd_eventos_sistema.nr_seq_regra_conv%type;
reg_integracao_w	gerar_int_padrao.reg_integracao_conv;
ie_geracao_w		intpd_fila_transmissao.ie_geracao%type;
caseno_w		hcm_fall.caseno%type;
movenumber_w		hcm_fall.movenumber%type;
sendclient_w		hcm_kopf.sendclient%type;
sendinstit_w		hcm_kopf.sendinstit%type;


BEGIN
intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);

/*retorna vazio pois e envio de novo registro para o is-h, ou seja, ainda nao temos codigo do is-h para enviar*/


select	a.nr_seq_documento,
	coalesce(b.ie_conversao,'I'),
	b.nr_seq_regra_conv,
	a.ie_geracao
into STRICT	nr_seq_documento_w,
	ie_conversao_w,
	nr_seq_regra_w,
	ie_geracao_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia = nr_seq_fila_p;

if (ie_geracao_w = 'U') then
	ish_get_manual_values(nr_seq_fila_p, r_get_enqueue_dequeue_w.patientid, caseno_w, movenumber_w, sendclient_w, sendinstit_w);
else
	begin
	begin
	select	cd_pessoa_fisica_externo
	into STRICT	r_get_enqueue_dequeue_w.patientid
	from	pf_codigo_externo
	where	cd_pessoa_fisica = nr_seq_documento_w
	and	ie_tipo_codigo_externo = 'ISH'  LIMIT 1;
	exception
	when others then
		r_get_enqueue_dequeue_w.patientid	:=	null;
	end;
	end;
end if;

reg_integracao_w.nm_tabela	:= 'PF_CODIGO_EXTERNO';
reg_integracao_w.nm_elemento	:= 'PatientEnqueue';

intpd_processar_atrib_envio(reg_integracao_w, 'CD_PESSOA_FISICA_EXTERNO', 'patientid', 'N', r_get_enqueue_dequeue_w.patientid, 'N', r_get_enqueue_dequeue_w.patientid);
CALL intpd_gravar_log_fila(reg_integracao_w);
RETURN NEXT r_get_enqueue_dequeue_w;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
