CREATE OR REPLACE FUNCTION ish_patient_pck.get_patient_cancel ( nr_seq_fila_p bigint) RETURNS SETOF T_PATIENT_CANCEL AS $body$
DECLARE


r_patient_cancel_w		r_patient_cancel;

nr_seq_documento_w	intpd_fila_transmissao.nr_seq_documento%type;
ie_conversao_w		intpd_eventos_sistema.ie_conversao%type;
nr_seq_regra_w		intpd_eventos_sistema.nr_seq_regra_conv%type;
cd_estab_doc intpd_fila_transmissao.cd_estab_documento%type;

cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
cd_empresa_w		estabelecimento.cd_empresa%type;
reg_integracao_w		gerar_int_padrao.reg_integracao_conv;


BEGIN
select	a.nr_seq_documento,
	coalesce(b.ie_conversao,'I'),
	b.nr_seq_regra_conv,
  a.cd_estab_documento
into STRICT	nr_seq_documento_w,
	ie_conversao_w,
	nr_seq_regra_w,
  cd_estab_doc
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia = nr_seq_fila_p;

cd_estabelecimento_w	:=	ish_patient_pck.get_patient_establishment(nr_seq_documento_w, cd_estab_doc);

select	cd_empresa
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento = cd_estabelecimento_w;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);

reg_integracao_w.nm_tabela	:= 'ESTABELECIMENTO';
reg_integracao_w.nm_elemento	:= 'PatientCancel';

intpd_processar_atrib_envio(reg_integracao_w, 'CD_EMPRESA','client', 'N', cd_empresa_w, 'S', r_patient_cancel_w.client);
intpd_processar_atrib_envio(reg_integracao_w, 'CD_ESTABELECIMENTO','institution', 'N', cd_estabelecimento_w, 'S', r_patient_cancel_w.institution);

begin
select	cd_pessoa_fisica_externo
into STRICT	r_patient_cancel_w.patientid
from	pf_codigo_externo
where	cd_pessoa_fisica = nr_seq_documento_w
and	ie_tipo_codigo_externo = 'ISH'  LIMIT 1;
exception
when others then
	r_patient_cancel_w.patientid	:=	null;
end;

reg_integracao_w.nm_tabela	:= 'PF_CODIGO_EXTERNO';
intpd_processar_atrib_envio(reg_integracao_w, 'CD_PESSOA_FISICA_EXTERNO','patientid', 'N', r_patient_cancel_w.patientid, 'N', r_patient_cancel_w.patientid);

--	testrun

if (length(r_patient_cancel_w.patientid) > 0) then
	RETURN NEXT r_patient_cancel_w;
end if;

CALL intpd_gravar_log_fila(reg_integracao_w);
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
