CREATE OR REPLACE FUNCTION ish_patient_pck.get_patient_establishment (nr_seq_documento_p bigint, cd_estab_doc_p bigint default null) RETURNS bigint AS $body$
DECLARE


cd_establishment_w			atendimento_paciente.cd_estabelecimento%type;			
cd_pessoa_fisica_w			atendimento_paciente.cd_pessoa_fisica%type;
nr_atendimento_w			atendimento_paciente.nr_atendimento%type;


BEGIN

cd_pessoa_fisica_w := nr_seq_documento_p;

select	max(nr_atendimento)
into STRICT	nr_atendimento_w
from	atendimento_paciente a
where	cd_pessoa_fisica = cd_pessoa_fisica_w
and	coalesce(dt_cancelamento::text, '') = '';

begin
select	cd_estabelecimento
into STRICT	cd_establishment_w
from	atendimento_paciente
where	nr_atendimento = nr_atendimento_w;
exception
when others then
	select	coalesce(cd_estab_doc_p, min(cd_estabelecimento))
  into STRICT	cd_establishment_w
	from	estabelecimento;
end;

return	cd_establishment_w;

END;

