CREATE OR REPLACE FUNCTION ish_patient_pck.get_patient_merge ( nr_seq_fila_p bigint) RETURNS SETOF T_PATIENT_MERGE AS $body$
DECLARE


r_patient_merge_w	MERGE_PAT;

cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;

reg_integracao_w		gerar_int_padrao.reg_integracao_conv;
nr_seq_documento_w		intpd_fila_transmissao.nr_seq_documento%type;
ie_geracao_w			intpd_fila_transmissao.ie_geracao%type;
cd_estab_doc intpd_fila_transmissao.cd_estab_documento%type;

pf_codigo_externo_dest_w	pf_codigo_externo%rowtype;
pf_codigo_externo_orig_w	pf_codigo_externo%rowtype;

cd_pessoa_destino_w 		varchar(10);
cd_pessoa_origem_w 		varchar(10);
ie_teste_w			varchar(1)	 := 'X';



BEGIN
begin
select	nr_seq_documento,
	ie_geracao
into STRICT	nr_seq_documento_w,
	ie_geracao_w
from	intpd_fila_transmissao
where	nr_sequencia = nr_seq_fila_p;
exception
when others then
	nr_seq_documento_w	:=	null;
end;

if (position('|' in nr_seq_documento_w) > 0) then
	cd_pessoa_destino_w	:=	obter_valor_campo_separador(nr_seq_documento_w, 1, '|');
	cd_pessoa_origem_w	:=	obter_valor_campo_separador(nr_seq_documento_w, 2, '|');
	ie_teste_w		:=	'X';
else
	begin
	select	cd_pessoa_fisica,
		cd_cadastro
	into STRICT	cd_pessoa_destino_w,
		cd_pessoa_origem_w
	from	pessoa_fisica_duplic
	where	cd_pessoa_fisica = cd_pessoa_destino_w;
	
	ie_teste_w		:=	null;
	exception
	when others then
		cd_pessoa_destino_w	:=	null;
		cd_pessoa_origem_w := null;
	end;
end if;

cd_estabelecimento_w	:=	ish_patient_pck.get_patient_establishment(cd_pessoa_destino_w, cd_estab_doc);

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);

reg_integracao_w.nm_tabela	:= 'ESTABELECIMENTO';
reg_integracao_w.nm_elemento	:= 'PatientMerge';

intpd_processar_atrib_envio(reg_integracao_w, 'CD_ESTABELECIMENTO','I_EINRI', 'N', cd_estabelecimento_w, 'S', r_patient_merge_w.EINR);

begin
select	*
into STRICT	pf_codigo_externo_orig_w
from	pf_codigo_externo
where	cd_pessoa_fisica = cd_pessoa_origem_w
and	ie_tipo_codigo_externo = 'ISH'  LIMIT 1;
exception
when others then
	pf_codigo_externo_orig_w	:=	null;
end;

begin
select	*
into STRICT	pf_codigo_externo_dest_w
from	pf_codigo_externo
where	cd_pessoa_fisica = cd_pessoa_destino_w
and	ie_tipo_codigo_externo = 'ISH'  LIMIT 1;
exception
when others then
	pf_codigo_externo_dest_w	:=	null;
end;

reg_integracao_w.nm_tabela	:= 'PF_CODIGO_EXTERNO';
intpd_processar_atrib_envio(reg_integracao_w, 'CD_PESSOA_FISICA_EXTERNO','I_PAT_NR', 'N', pf_codigo_externo_orig_w.cd_pessoa_fisica_externo, 'N', r_patient_merge_w.PAT_NR);
intpd_processar_atrib_envio(reg_integracao_w, 'CD_PESSOA_FISICA_EXTERNO','I_PAT_NR_ZIEL', 'N', pf_codigo_externo_dest_w.cd_pessoa_fisica_externo, 'N', r_patient_merge_w.PAT_NR_ZIEL);
intpd_processar_atrib_envio(reg_integracao_w, 'IE_TESTE','I_TEST', 'N', ie_teste_w, 'N', r_patient_merge_w.TEST);

RETURN NEXT r_patient_merge_w;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
