SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION ish_ppr_level_pck.get_ppr_level_cancel (nr_seq_fila_p bigint) RETURNS SETOF T_PPR_LEVEL AS $body$
DECLARE

	
nr_seq_documento_w		intpd_fila_transmissao.nr_seq_documento%type;
ie_conversao_w			intpd_eventos_sistema.ie_conversao%type;
nr_seq_regra_w			intpd_eventos_sistema.nr_seq_regra_conv%type;	

t_ppr_level_w			r_ppr_level;
reg_integracao_w		gerar_int_padrao.reg_integracao_conv;

cd_estab_w			estabelecimento.cd_estabelecimento%type;
cd_empresa_w			estabelecimento.cd_empresa%type;
nr_episodio_w			episodio_paciente.nr_episodio%type;
cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;
nr_atendimento_w		atendimento_paciente.nr_atendimento%type;
ie_nivel_compl_w		pe_prescricao.ie_nivel_compl%type;
ds_nivel_compl_w		varchar(255);
dt_inicio_prescr_w		timestamp;
dt_validade_prescr_w		timestamp;
	

BEGIN

begin
select	a.nr_seq_documento,
	coalesce(b.ie_conversao,'I'),
	b.nr_seq_regra_conv
into STRICT	nr_seq_documento_w,
	ie_conversao_w,
	nr_seq_regra_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia	= nr_seq_fila_p;
exception
when others then
	null;
end;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);

begin
select	b.nr_atendimento,
	a.cd_empresa,
	a.cd_estabelecimento,
	c.nr_episodio,
	b.cd_pessoa_fisica,
	d.ie_nivel_compl,
	d.dt_inicio_prescr,
	d.dt_validade_prescr
into STRICT	nr_atendimento_w,
	cd_empresa_w,
	cd_estab_w,
	nr_episodio_w,
	cd_pessoa_fisica_w,
	ie_nivel_compl_w,
	dt_inicio_prescr_w,
	dt_validade_prescr_w
FROM pe_prescricao d, estabelecimento a, atendimento_paciente b
LEFT OUTER JOIN episodio_paciente c ON (b.nr_seq_episodio = c.nr_sequencia)
WHERE b.cd_estabelecimento 	= a.cd_estabelecimento and b.nr_atendimento	= d.nr_atendimento and d.nr_sequencia		= nr_seq_documento_w;
exception
when others then
	null;
end;

reg_integracao_w.nm_tabela 	:= 'ESTABELECIMENTO';
reg_integracao_w.nm_elemento	:= '_-rzvish_-pprLevelCreate';

intpd_processar_atrib_envio(reg_integracao_w, 'CD_EMPRESA', 'client', 'N', cd_empresa_w, 'S', t_ppr_level_w.client);
intpd_processar_atrib_envio(reg_integracao_w, 'CD_ESTABELECIMENTO', 'institution', 'N', cd_estab_w, 'S', t_ppr_level_w.institution);

reg_integracao_w.nm_tabela 	:= 'EPISODIO_PACIENTE';
intpd_processar_atrib_envio(reg_integracao_w, 'NR_EPISODIO', 'patcaseid', 'N', nr_episodio_w, 'N', t_ppr_level_w.patcaseid);

t_ppr_level_w.klftyp	:= 'P';
t_ppr_level_w.klfart	:= 'E';

t_ppr_level_w.fklbdt	:= to_char(dt_inicio_prescr_w,'yyyy-mm-dd');
t_ppr_level_w.fkledt	:= to_char(dt_validade_prescr_w,'yyyy-mm-dd');

/*
ds_nivel_compl_w	:= obter_valor_dominio(8694,ie_nivel_compl_w);

t_ppr_level_w.pgra	:= substr(obter_valor_campo_separador(ds_nivel_compl_w,1,'/'),2,2);
t_ppr_level_w.pgrs	:= substr(obter_valor_campo_separador(ds_nivel_compl_w,2,'/'),2,2);*/
RETURN NEXT t_ppr_level_w;
CALL intpd_gravar_log_fila(reg_integracao_w);

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON FUNCTION ish_ppr_level_pck.get_ppr_level_cancel (nr_seq_fila_p bigint) FROM PUBLIC;
