SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION ish_rzv_insurance_pck.get_egk_get (nr_seq_fila_p bigint) RETURNS SETOF T_EGK_GET AS $body$
DECLARE



nr_seq_documento_w		intpd_fila_transmissao.nr_seq_documento%type;
ie_conversao_w			intpd_eventos_sistema.ie_conversao%type;
nr_seq_regra_w			intpd_eventos_sistema.nr_seq_regra_conv%type;
reg_integracao_w		gerar_int_padrao.reg_integracao_conv;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
cd_empresa_w			empresa.cd_empresa%type;
nr_episodio_w			episodio_paciente.nr_episodio%type;
r_egk_get_w			r_egk_get;

institute_w		hcm_versicherungsverhaltni.institute%type;
case_w			hcm_versicherungsverhaltni.caseno%type;
send_client_w		hcm_kopf.sendclient%type;
patientno_w		hcm_patienten.patientno%type;
movenumber_w		hcm_fall.movenumber%type;
ie_geracao_w		intpd_fila_transmissao.ie_geracao%type;


BEGIN
begin
select	a.nr_seq_documento,
	coalesce(b.ie_conversao,'I'),
	b.nr_seq_regra_conv,
	a.ie_geracao
into STRICT	nr_seq_documento_w,
	ie_conversao_w,
	nr_seq_regra_w,
	ie_geracao_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia	= nr_seq_fila_p;
exception
when others then
	null;
end;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);

if (ie_geracao_w = 'U') then
	begin
	ish_get_manual_values(nr_seq_fila_p, patientno_w, case_w, movenumber_w, send_client_w, institute_w);
	
	reg_integracao_w.nm_tabela 		:= 'ESTABELECIMENTO';
	reg_integracao_w.nm_elemento		:= '_-rzvish_-egkGet';

	intpd_processar_atrib_envio(reg_integracao_w, 'CD_EMPRESA', 'client', 'N', send_client_w, 'N', r_egk_get_w.client);
	intpd_processar_atrib_envio(reg_integracao_w, 'CD_ESTABELECIMENTO', 'institution', 'N', institute_w, 'N', r_egk_get_w.institution);

	reg_integracao_w.nm_tabela 		:= 'EPISODIO_PACIENTE';
	intpd_processar_atrib_envio(reg_integracao_w, 'NR_EPISODIO', 'patcaseid', 'N', case_w, 'N', r_egk_get_w.patcaseid);
	end;
else	
	begin
	cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;

	begin
	select	cd_empresa
	into STRICT	cd_empresa_w
	from	estabelecimento
	where	cd_estabelecimento = cd_estabelecimento_w;
	exception
	when others then
		cd_estabelecimento_w	:= null;
	end;

	begin
	select	caseno
	into STRICT	nr_episodio_w
	from 	hcm_fall
	where 	nr_sequencia = nr_seq_documento_w  LIMIT 1;
	exception
	when others then
		nr_episodio_w	:= null;
	end;
	
	reg_integracao_w.nm_tabela 		:= 'ESTABELECIMENTO';
	reg_integracao_w.nm_elemento		:= '_-rzvish_-egkGet';

	intpd_processar_atrib_envio(reg_integracao_w, 'CD_EMPRESA', 'client', 'N', cd_empresa_w, 'S', r_egk_get_w.client);
	intpd_processar_atrib_envio(reg_integracao_w, 'CD_ESTABELECIMENTO', 'institution', 'N', cd_estabelecimento_w, 'S', r_egk_get_w.institution);

	reg_integracao_w.nm_tabela 		:= 'EPISODIO_PACIENTE';
	intpd_processar_atrib_envio(reg_integracao_w, 'NR_EPISODIO', 'patcaseid', 'N', nr_episodio_w, 'N', r_egk_get_w.patcaseid);
	end;
end if;

RETURN NEXT r_egk_get_w;
CALL intpd_gravar_log_fila(reg_integracao_w);
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
