/*-----------------------------------------------------------------------------------------------------------------------------------------------------------------
'_-rzvish_-insuranceGet'
*/
CREATE OR REPLACE FUNCTION ish_rzv_insurance_pck.get_insurance_get (nr_seq_fila_p bigint) RETURNS SETOF T_INSURANCE_GET AS $body$
DECLARE


r_insurance_get_w	r_insurance_get;
nr_seq_hcm_vvf_w	hcm_versicherungsverhaltni.nr_sequencia%type;
nr_seq_registro_w	hcm_segmento.nr_seq_registro%type;
reg_integracao_w	gerar_int_padrao.reg_integracao_conv;
institute_w		hcm_versicherungsverhaltni.institute%type;
case_w			hcm_versicherungsverhaltni.caseno%type;
send_client_w		hcm_kopf.sendclient%type;
patientno_w		hcm_patienten.patientno%type;
movenumber_w		hcm_fall.movenumber%type;
ie_geracao_w		intpd_fila_transmissao.ie_geracao%type;


BEGIN

begin
select	nr_seq_documento,
	ie_geracao
into STRICT	nr_seq_hcm_vvf_w,
	ie_geracao_w
from	intpd_fila_transmissao
where	nr_sequencia = nr_seq_fila_p  LIMIT 1;
exception
when others then
	nr_seq_hcm_vvf_w := null;
end;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);
reg_integracao_w.nm_elemento			:= '_-rzvish_-insuranceGet';

if (ie_geracao_w = 'U') then
	ish_get_manual_values(nr_seq_fila_p, patientno_w, case_w, movenumber_w, send_client_w, institute_w);
else
	begin
	if (nr_seq_hcm_vvf_w IS NOT NULL AND nr_seq_hcm_vvf_w::text <> '') then
		begin
		begin
			select	a.institute,
				a.caseno,
				b.nr_seq_registro
			into STRICT	institute_w,
				case_w,
				nr_seq_registro_w
			from	hcm_versicherungsverhaltni a,
				hcm_segmento b
			where	a.nr_seq_segmento = b.nr_sequencia
			and	a.nr_sequencia = nr_seq_hcm_vvf_w  LIMIT 1;
		exception
		when others then
			r_insurance_get_w.institution	:= null;
			r_insurance_get_w.patcaseid	:= null;
			nr_seq_registro_w		:= null;
		end;
		
		begin
			select	a.sendclient
			into STRICT	send_client_w
			from	hcm_kopf a,
				hcm_segmento b
			where	a.nr_seq_segmento = b.nr_sequencia
			and	b.ie_tipo = 'HEA'
			and	b.nr_seq_registro = nr_seq_registro_w  LIMIT 1;
		exception
		when others then
			r_insurance_get_w.client := null;
		end;		
		end;
	end if;
	end;
end if;

reg_integracao_w.nm_tabela 	:= 'HCM_VERSICHERUNGSVERHALTNI';
intpd_processar_atrib_envio(reg_integracao_w, 'INSTITUTE', 'institution', 'N', institute_w, 'N', r_insurance_get_w.institution);
intpd_processar_atrib_envio(reg_integracao_w, 'CASENO', 'patcaseid', 'N', case_w, 'N', r_insurance_get_w.patcaseid);
reg_integracao_w.nm_tabela 	:= 'HCM_KOPF';
intpd_processar_atrib_envio(reg_integracao_w, 'SENDCLIENT', 'client', 'N', send_client_w, 'N', r_insurance_get_w.client);

RETURN NEXT r_insurance_get_w;
CALL intpd_gravar_log_fila(reg_integracao_w);
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
