SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION ish_rzv_insurance_pck.set_case_status (nr_seq_fila_p bigint) RETURNS SETOF T_CASE_STATUS AS $body$
DECLARE



nr_seq_documento_w		intpd_fila_transmissao.nr_seq_documento%type;
ie_conversao_w			intpd_eventos_sistema.ie_conversao%type;
nr_seq_regra_w			intpd_eventos_sistema.nr_seq_regra_conv%type;
reg_integracao_w		gerar_int_padrao.reg_integracao_conv;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
cd_empresa_w			empresa.cd_empresa%type;
nr_episodio_w			episodio_paciente.nr_episodio%type;
nr_seq_episodio_w		episodio_paciente.nr_sequencia%type;
ie_status_case_w		episodio_paciente_status.ie_status%type;
ie_status_previous_w		episodio_paciente_status.ie_status%type;
r_case_status_w			r_case_status;


BEGIN
begin
select	a.nr_seq_documento,
	coalesce(b.ie_conversao,'I'),
	b.nr_seq_regra_conv
into STRICT	nr_seq_documento_w,
	ie_conversao_w,
	nr_seq_regra_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia	= nr_seq_fila_p;
exception
when others then
	null;
end;

intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);

--cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;
select	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	atendimento_paciente
where	nr_seq_episodio	= nr_seq_documento_w;

begin
select	cd_empresa
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento = cd_estabelecimento_w;
exception
when others then
	cd_estabelecimento_w	:= null;
end;

begin
select	max(x.nr_episodio),
	max(x.nr_sequencia)
into STRICT	nr_episodio_w,
	nr_seq_episodio_w
from (
	SELECT	a.nr_episodio,
		a.nr_sequencia
	from	episodio_paciente a
	where	a.nr_sequencia = nr_seq_documento_w
	order	by a.nr_sequencia desc) x LIMIT 1;
exception
when others then
	nr_episodio_w	:= null;
end;

begin
select	ie_status
into STRICT	ie_status_case_w
from	episodio_paciente_status
where	nr_sequencia	= (
	SELECT	max(x.nr_sequencia)
	from	episodio_paciente_status x
	where	x.nr_seq_episodio = nr_seq_episodio_w);
exception
when others then
	ie_status_case_w	:= null;
end;

begin
select	ie_status
into STRICT	ie_status_previous_w
from	episodio_paciente_status
where	nr_sequencia = (
	SELECT	max(x.nr_sequencia)
	from	episodio_paciente_status x
	where	x.nr_seq_episodio = nr_seq_episodio_w
	and	x.nr_sequencia < (
			SELECT	max(y.nr_sequencia)
			from	episodio_paciente_status y
			where	y.nr_seq_episodio = nr_seq_episodio_w));
exception
when others then
	ie_status_previous_w	:= null;
end;

if	((coalesce(ie_status_previous_w,'NULL') = 'F') and (coalesce(ie_status_case_w,'NULL') = 'P')) then
	ie_status_case_w	:= 'A';
end if;

reg_integracao_w.nm_tabela 		:= 'ESTABELECIMENTO';
reg_integracao_w.nm_elemento		:= '_-rzvish_-setMdCaseStatus';

intpd_processar_atrib_envio(reg_integracao_w, 'CD_EMPRESA', 'client', 'N', cd_empresa_w, 'S', r_case_status_w.client);
intpd_processar_atrib_envio(reg_integracao_w, 'CD_ESTABELECIMENTO', 'institution', 'N', cd_estabelecimento_w, 'S', r_case_status_w.institution);

reg_integracao_w.nm_tabela 		:= 'EPISODIO_PACIENTE';
intpd_processar_atrib_envio(reg_integracao_w, 'NR_EPISODIO', 'patcaseid', 'N', nr_episodio_w, 'N', r_case_status_w.patcaseid);



reg_integracao_w.nm_tabela 		:= 'EPISODIO_PACIENTE_STATUS';
intpd_processar_atrib_envio(reg_integracao_w, 'IE_STATUS', 'txt04', 'N', ie_status_case_w, 'S', r_case_status_w.txt04);


RETURN NEXT r_case_status_w;
CALL intpd_gravar_log_fila(reg_integracao_w);
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON FUNCTION ish_rzv_insurance_pck.set_case_status (nr_seq_fila_p bigint) FROM PUBLIC;
