SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE ish_treatment_certificate_pck.get_treat_certificate_response (nr_sequencia_p bigint, ds_xml_p text) AS $body$
DECLARE

	
nr_seq_regra_w		intpd_eventos_sistema.nr_seq_regra_conv%type;
ie_conversao_w		intpd_eventos_sistema.ie_conversao%type;
reg_integracao_w	gerar_int_padrao.reg_integracao_conv;
nr_seq_sistema_w	intpd_eventos_sistema.nr_seq_sistema%type;
nr_seq_projeto_xml_w	intpd_eventos_sistema.nr_seq_projeto_xml%type;
ie_evento_w		intpd_fila_transmissao.ie_evento%type;

nr_seq_doc_origem_w	intpd_fila_transmissao.nr_seq_documento%type;
nr_seq_agrupador_w	intpd_fila_transmissao.nr_seq_agrupador%type;
nr_doc_externo_w	intpd_fila_transmissao.nr_doc_externo%type;
ds_xml_w		text;
xml_w			xml;
ds_erro_w		varchar(4000);

ie_status_w		intpd_fila_transmissao.ie_status%type	:=	'S';
ie_tipo_erro_w		intpd_fila_transmissao.ie_tipo_erro%type	:=	'F';
nr_sequencia_w		atendimento_paciente_inf.nr_sequencia%type;

c01 CURSOR FOR
SELECT 'CREATE' ie_tipo,
	a.DocNumber
from	xmltable(
		xmlnamespaces(
		'urn:sap-com:document:sap:soap:functions:mc-style' as "urn",
		'http://schemas.xmlsoap.org/soap/envelope/' as "soapenv"),
		'soapenv:Envelope/soapenv:Body/urn:_-rzvish_-certificTreatmCreateResponse' passing xml_w columns
	DocNumber 	varchar(30) path 'DocNumber') a
where	length(a.DocNumber) > 0

union

select 'CHANGE' ie_tipo,
	a.DocNumber
from	xmltable(
		xmlnamespaces(
		'urn:sap-com:document:sap:soap:functions:mc-style' as "urn",
		'http://schemas.xmlsoap.org/soap/envelope/' as "soapenv"),
		'soapenv:Envelope/soapenv:Body/urn:_-rzvish_-certificTreatmChangeResponse' passing xml_w columns
	DocNumber 	varchar(30) path 'DocNumber') a
where	length(a.DocNumber) > 0

union

select 'CANCEL' ie_tipo,
	a.DocNumber
from	xmltable(
		xmlnamespaces(
		'urn:sap-com:document:sap:soap:functions:mc-style' as "urn",
		'http://schemas.xmlsoap.org/soap/envelope/' as "soapenv"),
		'soapenv:Envelope/soapenv:Body/urn:_-rzvish_-certificTreatmCancelResponse' passing xml_w columns
	DocNumber 	varchar(30) path 'WorstReturnedMsgty') a;
	
c01_w	c01%rowtype;
	

BEGIN
intpd_reg_integracao_inicio(nr_sequencia_p, 'R', reg_integracao_w);

delete	FROM intpd_log_recebimento
where	nr_seq_fila = nr_sequencia_p;

begin
select	a.nr_seq_documento,
	coalesce(b.ie_conversao,'I'),
	b.nr_seq_sistema,
	b.nr_seq_projeto_xml,
	b.nr_seq_regra_conv,	
	a.ie_evento,
	a.nr_seq_agrupador
into STRICT	nr_seq_doc_origem_w,
	ie_conversao_w,
	nr_seq_sistema_w,
	nr_seq_projeto_xml_w,
	nr_seq_regra_w,	
	ie_evento_w,
	nr_seq_agrupador_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia = nr_sequencia_p;
exception
when others then
	null;
end;

ish_converter_response(nr_sequencia_p, ds_xml_p, ie_status_w, ie_tipo_erro_w, xml_w);

if (ie_status_w = 'E') then
	update	intpd_fila_transmissao
	set	ie_status 		= ie_status_w,
		ie_tipo_erro 		= ie_tipo_erro_w,
		ie_response_procedure 	= 'S',
		ds_xml_retorno		= ds_xml_p
	where	nr_sequencia 		= nr_sequencia_p;
else
	begin	
	open C01;
	loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	
		select	max(a.nr_sequencia)
		into STRICT	nr_sequencia_w
		from	atendimento_paciente_inf a
		where	a.nr_sequencia	=
			(SELECT max(x.nr_sequencia)
			from	atendimento_paciente_inf x
			where	x.nr_atendimento = nr_seq_doc_origem_w);
		
		if (c01_w.ie_tipo = 'CREATE')  or (c01_w.ie_tipo = 'CHANGE')  then
	
			nr_doc_externo_w := c01_w.DocNumber;
		
			CALL gerar_conv_meio_externo(null,
						'ATENDIMENTO_PACIENTE_INF',
						'NR_SEQUENCIA',
						nr_sequencia_w,
						c01_w.DocNumber,
						null,
						nr_seq_regra_w,
						'A',
						'INTPDTASY');

		elsif (c01_w.ie_tipo = 'CANCEL') then
		
			delete	from	conversao_meio_externo
			where	nr_seq_regra	= nr_seq_regra_w
			and	nm_tabela	= 'ATENDIMENTO_PACIENTE_INF'
			and	nm_atributo	= 'NR_SEQUENCIA'
			and	cd_interno	= nr_sequencia_w;
			
		end if;
	
	end loop;
	close C01;
	
	update	intpd_fila_transmissao
	set	ie_status 		= coalesce(ie_status_w,'S'),
		ie_response_procedure	= 'S',
		nr_doc_externo		= nr_doc_externo_w,
		ds_xml_retorno		= ds_xml_p
	where	nr_sequencia 		= nr_sequencia_p;

	exception
	when others then
		ds_erro_w	:=	substr(dbms_utility.format_error_backtrace || chr(13) || chr(10) || sqlerrm,1,4000);

		rollback;

		update	intpd_fila_transmissao
		set	ie_status 		= 'E',
			ie_response_procedure	= 'S',
			ds_log 			= ds_erro_w,
			ds_xml_retorno		= ds_xml_p
		where	nr_sequencia 		= nr_sequencia_p;
	end;


end if;

if (nr_seq_agrupador_w > 0) then
	CALL intpd_processar_fila_trans(null, 'S', nr_seq_agrupador_w);
end if;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ish_treatment_certificate_pck.get_treat_certificate_response (nr_sequencia_p bigint, ds_xml_p text) FROM PUBLIC;
