CREATE OR REPLACE FUNCTION ish_utils_pck.get_ish_valid_time ( ds_date_p text, ds_time_p text) RETURNS timestamp AS $body$
DECLARE

			
ds_time_return_w	varchar(8);
dt_return_w		timestamp;
			

BEGIN

if (ds_time_p = '24:00:00') then
	ds_time_return_w	:= '23:59:59';
else
	ds_time_return_w	:= ds_time_p;
end if;

dt_return_w	:= to_date(ds_date_p || ds_time_return_w,'yyyy-mm-ddhh24:mi:ss');

if (trunc(dt_return_w,'yyyy') = to_date('01/01/9999','dd/mm/yyyy')) then
	dt_return_w	:= null;
end if;

return dt_return_w;

end;


$body$
