CREATE OR REPLACE FUNCTION ish_utils_pck.obter_dados_fila_atx ( nr_seq_documento_p text, ie_evento_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_episodio_w	atendimento_paciente.nr_seq_episodio%type	:= null;
nr_atendimento_w	atendimento_paciente.nr_atendimento%type	:= null;
cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type		:= null;
ds_separador_w		varchar(10)					:= ish_param_pck.get_separador;
BEGIN
if (ie_evento_p in ('106','220','401')) then
	nr_seq_episodio_w	:= nr_seq_documento_p;
elsif (ie_evento_p in ('114','120','127','402')) then
	nr_atendimento_w	:= nr_seq_documento_p;
elsif (ie_evento_p in ('82','135','190')) then
	nr_atendimento_w	:= obter_valor_campo_separador(nr_seq_documento_p, 1, ds_separador_w);
	if (ie_evento_p in ('135')) then
		nr_seq_episodio_w	:= obter_valor_campo_separador(nr_seq_documento_p, 3, ds_separador_w);
	end if;
elsif (ie_evento_p in ('360')) then
	begin
	select	cd_pessoa_fisica
	into STRICT	cd_pessoa_fisica_w
	from	pessoa_fisica_egk
	where	nr_sequencia	= nr_seq_documento_p;
	exception
	when others then
		cd_pessoa_fisica_w	:= null;
	end;
	if (coalesce(cd_pessoa_fisica_w::text, '') = '') then
		nr_seq_episodio_w	:= obter_valor_campo_separador(nr_seq_documento_p, 1, ds_separador_w);
	end if;
elsif (ie_evento_p in ('12')) then
	cd_pessoa_fisica_w	:= nr_seq_documento_p;
elsif (ie_evento_p in ('54')) then
	select	max(nr_atendimento)
	into STRICT	nr_atendimento_w
	from	procedimento_paciente
	where	nr_atendimento	= nr_seq_documento_p;
elsif (ie_evento_p in ('118'))then
	select	nr_atendimento
	into STRICT	nr_atendimento_w
	from	atend_paciente_unidade
	where	nr_seq_interno	= nr_seq_documento_p;
elsif (ie_evento_p in ('185')) then
	select	nr_sequencia
	into STRICT	nr_seq_episodio_w
	from	episodio_paciente
	where	nr_episodio	= nr_seq_documento_p;
elsif (ie_evento_p in ('403')) then
	begin
	select	nr_atendimento
	into STRICT	nr_atendimento_w
	from	atendimento_paciente_inf
	where	nr_sequencia	= nr_seq_documento_p;
	exception
	when others then
		nr_atendimento_w	:= null;
	end;
	
	if (coalesce(nr_atendimento_w::text, '') = '') then
		nr_atendimento_w	:= nr_seq_documento_p;
	end if;
end if;

if (ie_opcao_p = 'A') then
	return	nr_atendimento_w;
elsif (ie_opcao_p = 'E') then
	return	nr_seq_episodio_w;
elsif (ie_opcao_p = 'P') then
	return	cd_pessoa_fisica_w;
else
	return null;
end if;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
