-- Close file after editing
CREATE OR REPLACE PROCEDURE jpn_utl_file_pck.close_file (ie_fechar_p text default 'U') AS $body$
BEGIN
-- close file
if (ie_fechar_p = 'U') then
	utl_file.fclose(ds_arquivo_w);

-- Close all files in the session
elsif (ie_fechar_p = 'T') then
	utl_file.fclose_all;
end if;

END;

