-- write content to the file
CREATE OR REPLACE PROCEDURE jpn_utl_file_pck.write_file ( ds_conteudo_p text ) AS $body$
DECLARE

ds_content_w varchar(32767);

BEGIN
-- Check if the file is open for editing
if (utl_file.is_open(ds_arquivo_w)) then
	-- write_file the information in the file
	begin
    ds_content_w := ds_conteudo_p;
	utl_file.put_raw( ds_arquivo_w, utl_i18n.string_to_raw( ds_content_w , 'JA16SJIS'));
