CREATE OR REPLACE PROCEDURE kodip_pck.clear_case_drg ( reg_integracao_p INOUT gerar_int_padrao.reg_integracao_conv, nr_seq_episodio_p bigint, nm_usuario_p text) AS $body$
DECLARE

		
ds_erro_w			varchar(4000);		
			
c01 CURSOR FOR
SELECT	nr_sequencia,
		nr_seq_propaci
from		episodio_paciente_drg
where	nr_seq_episodio_paciente = nr_seq_episodio_p
and		ie_situacao	= 'A';

c01_w	c01%rowtype;			


BEGIN
-- desativa os drgs anterios do case
open c01;
loop
fetch c01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	update	episodio_paciente_drg
	set	ie_situacao = 'I'
	where	nr_sequencia = c01_w.nr_sequencia;
	
	if (c01_w.nr_seq_propaci IS NOT NULL AND c01_w.nr_seq_propaci::text <> '') then
		CALL excluir_proc_pac_drg(c01_w.nr_seq_propaci, nm_usuario_p);
	end if;	
	
	commit;
	exception
	when others then
		begin
		ds_erro_w	:=	substr('Error clear_case_drg: ' || sqlerrm || chr(13) || chr(10) ||
						dbms_utility.format_error_backtrace,1, 2000);
		CALL CALL kodip_pck.incluir_log(reg_integracao_p, ds_erro_w, 'KODIP-RECEIVE', 'W');
		rollback;
		end;
	end;
end loop;
close c01;

END;

$body$
