CREATE OR REPLACE PROCEDURE kodip_pck.clear_ze_drg ( reg_integracao_p INOUT gerar_int_padrao.reg_integracao_conv, nr_seq_episodio_p bigint, nm_usuario_p text) AS $body$
DECLARE

		
ds_erro_w		varchar(4000);
			

c01 CURSOR FOR
SELECT	a.nr_seq_propaci
from	episodio_paciente_drg_item a,
	episodio_paciente_drg b,
	procedimento_paciente c
where	a.nr_seq_pac_drg		= b.nr_sequencia
and	a.nr_seq_propaci		= c.nr_sequencia
and	coalesce(c.cd_motivo_exc_conta::text, '') = ''
and	a.ie_tipo_item			= 'TAX'
and	b.nr_seq_episodio_paciente 	= nr_seq_episodio_p;

c01_w	c01%rowtype;			


BEGIN
--excluir os procedimentos de taxa (ze..) que foram gerados para alguma outra drg
open c01;
loop
fetch c01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	CALL excluir_proc_pac_drg(c01_w.nr_seq_propaci, nm_usuario_p);
	commit;
	exception
	when others then
		begin
		ds_erro_w	:=	substr('Error clear_ze_drg: ' || sqlerrm || chr(13) || chr(10) ||
						dbms_utility.format_error_backtrace,1, 2000);
		CALL CALL kodip_pck.incluir_log(reg_integracao_p, ds_erro_w, 'KODIP-RECEIVE', 'W');
		rollback;
		end;
	end;
end loop;
close c01;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
