CREATE OR REPLACE FUNCTION kodip_pck.get_error ( nr_seq_documento_p text, nr_atendimento_p bigint) RETURNS SETOF T_ERROR AS $body$
DECLARE


_ora2pg_r RECORD;
nr_seq_episodio_w		episodio_paciente.nr_sequencia%type;
