CREATE OR REPLACE FUNCTION kodip_pck.get_submit_diag () RETURNS varchar AS $body$
DECLARE


ie_liberar_diagnostico_w	parametro_drg.ie_liberar_diagnostico%type;

BEGIN
begin
select	coalesce(ie_liberar_diagnostico, 'N')
into STRICT	ie_liberar_diagnostico_w
from	parametro_drg
where	cd_estabelecimento = obter_estabelecimento_ativo  LIMIT 1;
exception
when others then
	ie_liberar_diagnostico_w	:=	'N';
end;

return ie_liberar_diagnostico_w;
END;

$body$
